INSERT INTO `board` (`b_no`, `b_title`, `b_note`, `b_writer`, `b_date`, `b_link`) VALUES
	(1, '가돌리늄 함유 제제 (미국 FDA 의약품 안전성 서한)', '2018-05-25\n\n￭ 요약\n미국 FDA는 가돌리늄 조영제 투여 후 가돌리늄이 뇌를 포함한 체내에 잔류하는 보고와 관련하여 새로운 안전성 조치를 발표함\n\n￭ 상세정보\n가돌리늄 조영제 투여 후 뇌, 뼈, 피부 및 그 외 다른 장기에 가돌리늄이 잔류함\n- 가돌리늄 조영제는 신장을 통해서 대부분 배설되나 미량의 가돌리늄이 장기간 체내에 남을 수 있음\n- 가돌리늄 체내 잔류로 인한 임상적 결과는 확립되지 않음\n\n￭ 조치사항\n식품의약품안전처는 유럽, 미국, 일본 등 해외의 조치, 국내·외 현황 검토 등의 절차를 거쳐 해당 품목에 필요한 안전조치를 취할 계획임', 'admin', '2018-05-24 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=65&search_value=&search_term=&paging_value=5&setLine=&setHeader='),
	(2, '벤조카인 함유 제제 (미국 FDA 의약품 안전성 서한)', '2018-05-28\n\n￭ 요약\n●미국 FDA는 구강 국소 마취 등에 사용하는 벤조카인 함유 제제를 24개월 미만 영아에 사용 금지 조치함\n\n￭ 상세정보\n● 국소 마취제로 사용되는 벤조카인 함유 제제는 혈액을 통해 운반되는 산소의 양을 크게 감소시키는 메트헤모글로빈혈증을 유발할 수 있으며, 이는 사망까지 이르는 치명적인 결과를 가지고 올 수 있음\n● 미국 FDA는 이에 대한 안전성 문제로 벤조카인을 함유한 24개월 미만 영아에 사용하는 제품의 시판을 중단토록 조치하고\n- 24개월 이상 어린이 및 성인에게 사용하는 제품에도 다음과 같이 라벨을 변경토록함\n· 메트헤모글로빈혈증에 대한 위험 경고 추가\n· 24개월 미만 영아에게 사용하지 말 것\n· 부모 및 보호자가 24개월 미만 영아에게 사용하지 않도록 함\n\n￭ 조치사항\n● 식품의약품안전처는 동 제제의 허가사항에 메트헤모글로빈혈증에 대한 경고를 강화하고 24개월 미만 영아에게 사용금지하도록 하는 등 안전성 강화 조치를 취할 계획임', 'admin', '2018-05-27 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=66&search_value=&search_term=&paging_value=5&setLine=&setHeader='),
	(3, '발사르탄 함유 제제(식약처 의약품 안전성 서한)', '2018-07-07\n\n￭ 요약\nO 유럽의약안전청(EMA)은 N-니트로소디메틸아민이 검출된 Zhejiang Huahai Pharmaceutical Co.,Ltd(중국) 사 발사르탄(Valsartan) 원료를 사용한 고혈압치료제에 대해 회수중임을 발표함\n- 관련 제품에 대한 잠정 판매중지 및 제조중지 조치\n\n￭ 상세정보\nO 유럽의약안전청(EMA)가 중국산 원료의약품에 발암 추정 불순물 함유를 이유로 해당 원료 사용 의약품 회수 중임을 발표\nO 관련 제품 219품목에 대한 잠정 판매중지 및 제조중지 조치함\n- 현재 투약중인 환자는 제품의 사용을 자의적으로 중단하지 말고 담당의사와 상의할 것\n- 전문가는 조치대상 의약품 복용 환자에 대해 조치 비대상 품목으로의 전환 또는 대체의약품 처방 권고\n\n￭ 조치사항\nO 식품의약품안전처는 국내/외 관련 정보 지속 확인 및 안전관리 조치 예정임', 'admin', '2018-07-06 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=67&search_value=&search_term=&paging_value=5&setLine=&setHeader='),
	(4, '플루오로퀴놀론계(Fluoroquinolone) 항생제 (미국 FDA 의약품 안전성 서한)', '2018-07-13\n\n￭ 요약\n● 미국 FDA는 ‘플루오로퀴놀론계(Fluoroquinolone) 항생제‘ 복용 시 심각한 ‘저혈당’ 및 ‘정신장애’ 가 유발될 수 있음을 알리는 안전성 서한 배포\n\n￭ 상세정보\n● 감염증 치료제인 플루오로퀴놀론계 항생제(경구제 및 주사제)가 심각한 저혈당 및 정신장애를 초래할 수 있음\n- 저혈당은 특히, 노인 및 혈당강하제를 복용하는 당뇨환자에서 혼수 증상을 비롯한 심각한 문제를 야기할 수 있음\n- 이미 중추신경계 이상반응이 허가사항에 반영되어 있으나, ‘주의력 장애, 방향감각 상실, 초조, 신경과민, 기억력 장애 및 섬망’ 등을 새롭게 추가함\n\n￭ 조치사항\n플루오로퀴놀론계 항생제의 국내 허가사항에는 ‘저혈당’이 포함되어 있으나 저혈당으로 인한 \'혼수\'는 명시되어 있지 않아 식품의약품안전처는 이를 허가사항에 반영할 계획이며\n- 이에 앞서 전문가 및 환자에게 주의를 당부하고자 안전성 서한을 배포함', 'admin', '2018-07-12 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=68&search_value=&search_term=&paging_value=5&setLine=&setHeader='),
	(5, '가돌리늄 조영제(식약처 의약품 안전성 서한)', '2018-08-09\n\n￭ 요약\n우리처(의약품안전평가과)는 가돌리늄 조영제 국외 안전성 정보에 따라 붙임과 같이 안전성 서한을 배포합니다.', 'admin', '2018-08-08 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=70&search_value=&search_term=&paging_value=5&setLine=&setHeader='),
	(6, 'SGLT2 저해제 (미국 FDA 의약품 안전성 서한)', '2018-08-31\n\n￭ 요약\n우리처(의약품안전평가과)는 SGLT2 저해제 국외 안전성 정보에 따라 붙임과 같이 안전성 서한을 배포합니다.', 'admin', '2018-08-30 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=72&search_value=&search_term=&paging_value=5&setLine=&setHeader='),
	(7, '비페닐디메틸디카르복실레이트·마늘유 함유 제제 (식약처 의약품 안전성 서한)', '2018-09-10\n\n￭ 요약\n임상재평가 실시 결과와 관련하여 \'비페닐디메틸디카르복실레이트·마늘유\'성분 제제의 의약품 안전성 서한을 붙임과 같이 발행', 'admin', '2018-09-09 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=73&search_value=&search_term=&paging_value=5&setLine=&setHeader='),
	(8, '히드로클로로티아지드 제제 (유럽 의약품청(EMA) 의약품 안전성 서한)', '2018-12-07\n\n￭ 요약\n유럽 의약품청(EMA)의 히드로클로로티아지드 함유 제제에 대한 안전성 정보와 관련하여 붙임과 같이 안전성 서한을 배포합니다.', 'admin', '2018-12-06 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=74&search_value=&search_term=&paging_value=5&setLine=&setHeader='),
	(9, '플루오로퀴놀론 제제 (미국 FDA 의약품 안전성 서한)', '2018-12-21\n\n￭ 요약\n미국 FDA의 의약품 안전성 정보와 관련하여 붙임과 같이 의약품안전성 서한을 배포합니다.', 'admin', '2018-12-20 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=75&search_value=&search_term=&paging_value=5&setLine=&setHeader='),
	(10, '독감치료제 \'오셀타미비르인산염’제제 (식약처 의약품 안전성 서한)', '2018-12-24\n\n￭ 요약\n오셀타미비르제제 의약품 안전성 정보와 관련하여 붙임과 같이 의약품안전성 서한을 배포합니다.', 'admin', '2018-12-23 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=76&search_value=&search_term=&paging_value=5&setLine=&setHeader='),
	(11, '통풍치료제 \'페북소스타트’성분 제제 (미국 FDA 의약품 안전성 서한)', '2019-02-25\n\n￭ 요약\n□ 정보원\n❍ 미국 FDA는 페북소스타트 제제에 대한 시판 후 임상시험 결과 다른 통풍치료제 알로푸리놀 제제에 비해 사망 위험이 높다는 것을 확인하고 관련 정보를 발표함\n\n￭ 상세정보\n□ 주요내용\n❍ 미국 FDA는 페북소스타트 제제의 안전성 평가를 위한 시판 후 임상시험자료 검토 결과 알로푸리놀에 비해 사망 위험성이 증가한다는 것을 확인하였음\n- 이에 관련 정보를 업데이트하기 위하여 제품 설명서 경고 항을 추가할 예정이며,\n- 페북소스타트 제제는 알로푸리놀 제제 복용 시 심각한 부작용이 나타나거나 알로푸리놀이 효과적이지 않은 환자에게만 사용토록 제한할 예정임\n❍ 국내 페북소스타트 허가사항에는 알로푸리놀에 비해 심혈관계 부작용 발생빈도가 높다는 사실과 심혈관계 증상을 주의 깊게 모니터링하라는 정보가 반영되어 있으나\n- 식품의약품안전처는 전문가 및 환자에게 관련 정보를 환기시키고 주의사항을 당부하기 위해 안전성서한을 배포하며,\n- 추가로 국내외 허가현황 및 사용실태 등을 종합적으로 검토하여 허가사항 변경 등을 진행할 예정임\n\n￭ 조치사항\n□ 전문가를 위한 정보\n❍ 환자에게 이 약 복용 시 나타 날 수 있는 심혈관계 위험성을 알리고 이상증상*이 나타나는 경우 즉시 진료 받도록 안내하시기 바람\n* 가슴통증, 호흡곤란, 빠르거나 불규칙한 심장박동, 신체 한쪽 측면의 쇠약감 또는 저림(무감각), 현기증, 갑작스런 심한 두통, 언어장애\n❍ 심장질환 및 뇌졸중 병력이 있는 환자는 이 약의 위험성 유익성을 고려하여 사용하시기 바람\n❍ 이 약은 알로푸리놀에 의해 심각한 부작용이 발생하거나 알로푸리놀이 효과적이지 않은 환자에 투여하는 것을 고려하시기 바람\n❍ 이 약의 사용 시 나타나는 부작용은 한국의약품안전관리원에 보고하시기 바람\n□ 환자를 위한 정보\n❍ 심장질환 및 뇌졸중의 병력이 있는 환자는 전문가에게 그 사실을 알리시기 바람\n❍ 이 약을 복용하는 다음 증상이 나타날 경우 즉시 진료를 받으시기 바람\n- 가슴통증 호흡곤란 빠르거나 불규칙한 심장박동 신체 한쪽 측면의 쇠약감 또는 저림 무감각 현기증 갑작스런 심한 두통, 언어장애\n❍ 증상이 악화될 수 있으므로 전문가와 상의없이 이 약의 복용을 중단하지 마시기 바람\n❍ 이 약의 사용 시 나타나는 부작용은 한국의약품안전관리원에 보고하시기 바람', 'admin', '2019-02-24 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=77&search_value=&search_term=&paging_value=5&setLine=&setHeader='),
	(12, '설사치료제 \'디옥타헤드랄스멕타이트’ 제제 (식약처 의약품 안전성 서한)', '2019-04-23\n\n￭ 요약\n□ 정보원\n❍ 프랑스 국립의약품건강제품안전청(ANSM)은 ‘디옥타헤드랄스멕타이트’ 성분 제제에 대해 미량의 납 함유 가능성을 고려하여 예방조치로 ‘만 2세 미만 소아, 임부 및 수유부’에게 사용하지 말 것을 발표함\n\n￭ 상세정보\n□ 주요내용\n❍ 프랑스 ANSM은 ‘디옥타헤드랄스멕타이트’ 성분 제제의 혈중으로의 납 이행 여부를 검토하였음\n- 검토 결과 성인은 혈중으로의 납 이행 위험이 없으나, 만 2세 미만 소아는 위험을 배제할 수 없다고 판단하였음\n- 이에 예방 조치로 제품 설명서에 ‘만 2세 미만 소아, 임부 및 수유부에게 이 약을 사용하지 말 것’을 추가하였음\n- 아울러, ‘만 2세 이상 소아’에게는 급성 설사 치료를 위해서만 사용하고 투여기간은 7일 이내로 제한하였음\n❍ 식품의약품안전처는 국내외 허가현황 및 사용실태 등을 종합적으로 검토하여 국내 제품의 허가사항 변경절차를 진행 중이며\n- 허가사항 변경 전까지 전문가 및 환자에게 관련 정보를 알리고 주의를 당부하고자 안전성 서한을 배포함\n□ 대상품목 현황\n❍ 디옥타헤드랄스멕타이트 성분 제제\n- ㈜대웅제약, 스멕타현탁액 등 총 8개 업체 8품목\n\n￭ 조치사항\n□ 전문가를 위한 정보\n❍ 프랑스 임상시험은 성인을 대상으로 실시하여 이를 소아에게 모델링하여 결론을 얻은 것으로, 2세 미만 등에 사용을 제한하는 것은 예방적 조치임을 이해하시기 바람\n❍ 이 약을 만 2세 미만 소아, 임부 및 수유부에게 사용하지 마시기 바람\n❍ 만 2세 이상 소아에게는 급성 설사 치료를 위해서만 사용하고 투여기간은 7일 이내로 제한하시기 바람\n- 성인은 ‘식도, 위‧십이지장과 관련된 통증의 완화, 급‧만성 설사’에 사용제한 없음\n❍ 이 약 사용 시 나타나는 부작용은 한국의약품안전관리원에 보고하시기 바람\n□ 환자를 위한 정보\n❍ 만 2세 미만 소아, 임부 및 수유부는 이 약을 사용하지 마시기 바람\n❍ 만 2세 이상 소아의 경우, 급성 설사 치료를 위해서만 사용하고 최대 7일까지만 복용하시기 바람\n❍ 이 약을 복용 중인 경우 임의로 중단하지 말고 의사, 약사와 상의하시기 바람\n❍ 이 약 사용 시 나타나는 부작용은 한국의약품안전관리원에 보고하시기 바람', 'admin', '2019-04-22 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=79&search_value=&search_term=&paging_value=5&setLine=&setHeader='),
	(13, '테르토모타이드염산염 함유 주사제 (식약처 의약품 안전성 서한)', '2019-08-23\n\n￭ 요약\n삼성제약(주)에서 시판 중인 “테르토모타이드염산염” 함유 주사제에 대한 3상 임상시험 수행 중 실시한 중간분석 결과, 유의성이 관찰되지 않아 무용성 결론에 따른 조치사항 알림\n\n￭ 상세정보\n❍ 삼성제약(주)에서 시판 중인 “테르토모타이드염산염” 함유제제에 대한 안전정보는 다음과 같음\n- 실시 중인 3상 임상시험 중간분석에서 안전성에 대한 문제는 없으나 유의성이 관찰되지 않아 무용성 결론\n- 임상시험에 참여 중인 환자의 투여중단 여부에 대한 의료진의 종합적인 판단을 위해 중간분석 결과 제공\n❍ 해당 임상시험에 대한 최종 결과분석 완료 시 까지 시판품에 대한 신규 환자의 사용 제한\n\n￭ 조치사항\n□ 의약전문가를 위한 권고사항\n❍ 현재 해당 품목 관련 3상 임상시험 중 실시한 중간분석결과 무용성 결론에 따라, 현재 해당 품목을 투여 중인 환자에게 동 정보내용을 알리고 지속적인 투여 여부에 대하여 신중히 결정할 것\n□ 환자를 위한 권고사항\n❍ 현재 해당 제품을 투여받는 환자는 해당 의약품 성분 및 다른 대체 치료 방법 등에 대하여 담당 의사와 상담할 것', 'admin', '2019-08-22 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=82&search_value=&search_term=&paging_value=4&setLine=&setHeader='),
	(14, '라니티딘 제제(식약처 의약품 안전성 서한)', '2019-09-26\n\n￭ 요약\n라니티딘 함유 제품에 대해 제조·수입·판매·처방 잠정 중지 조치\n- N-니트로소디메틸아민(NDMA) 잠정관리기준 초과 검출 -\n\n￭ 상세정보\n❍ 식약처는 국내 수입·제조되는 SMS Lifesciences 등 7개 업체의 라니티딘 원료를 조사하고, 해당 7개 원료에서 NDMA가 잠정관리기준(0.16ppm)을 초과하여 검출됨을 확인\n- 해당 원료와 이를 사용한 완제의약품에 대해 잠정 제조·수입중지 및 판매중지 조치함\n- 이는 사전 예방적 차원의 잠정조치임\n❍ 참고로, 향후 추가적으로 확인되는 국내·외 안전성 정보에 따라 후속 조치가 진행될 예정임\n\n￭ 조치사항\n❍ 인도 SMS Lifesciences 社 등 7개 업체*에서 제조한 라니티딘 원료 사용이 확인된 국내 133개사 269개 완제의약품(붙임참고)', 'admin', '2019-09-25 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=83&search_value=&search_term=&paging_value=4&setLine=&setHeader='),
	(15, '니자티딘 제제(식약처 의약품 안전성 서한)', '2019-11-22\n\n￭ 요약\n니자티딘 일부 의약품(13품목)에 대해 제조·판매·처방 잠정 중지 조치\n- N-니트로소디메틸아민(NDMA) 잠정관리기준 초과 검출 -\n\n￭ 상세정보\n❍ 식약처는 국내 수입·제조되는 니자티딘 4종의 원료를 조사하였고, 일부 원료 제조번호에서 NDMA가 잠정관리기준(0.32ppm)을 미량 초과하여 검출됨을 확인\n- 해당 원료를 사용한 완제의약품 전체에 대한 수거검사를 실시하였고, 13개 완제의약품에서 NDMA 미량 초과 검출* 확인 * 0.34∼1.43ppm 검출 (잠정관리기준 0.32ppm)\n- 해당 완제의약품 13개 품목에 대해 잠정 제조중지 및 판매중지 조치함\n- 이는 사전 예방적 차원의 잠정조치임\n\n￭ 조치사항\n❍ 니자티딘 NDMA 초과 검출이 확인된 10개사 13개 완제의약품에 대해 잠정 제조중지 및 판매중지 조치함(붙임참고)', 'admin', '2019-11-21 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=84&search_value=&search_term=&paging_value=4&setLine=&setHeader='),
	(16, '가바펜티노이느계 성분제제(식약처 의약품 안전성 서한)', '2019-12-23\n\n￭ 요약\n□ 정보원\n❍ 미국 식품의약품청(FDA)은 ‘가바펜티노이드계’ 성분제제를 호흡기계 위험요인이 있는 환자에게 투여시 심각한 호흡곤란이 발생할 위험이 있음을 확인하고 관련 정보를 발표함\n\n￭ 상세정보\n□ 주요내용\n❍ 미국 FDA는 ‘가바펜티노이드계’ 성분제제의 이상반응 사례와 임상‧비임상 자료 검토 결과, 호흡기계 위험요인이 있는 환자에서 심각한 호흡곤란 발생 위험이 있음을 확인하였음\n* 가바펜티노이드계 성분: 가바펜틴, 프레가발린\n* 호흡기계 위험요인: 중추신경계억제제 병용투여(오피오이드계 진통제, 항불안제, 항우울제, 항히스타민제 등), 폐기능 감소 상태, 고령자 등\n- 가바펜티노이드계 약물을 단독 투여하는 건강한 환자는 심각한 호흡곤란 발생 위험의 근거가 적음\n- 이에 관련 정보를 업데이트하기 위하여 제품 설명서 ‘경고’항에 추가할 예정이며,\n- 제품 허가권자에게 안전성 평가 임상시험 실시를 요청하였음\n❍ 국내 ‘가바펜틴, 프레가발린’ 성분제제의 허가사항에는 중추신경계억제제와 병용투여한 환자에서 호흡억제 및 진정 등이 발생할 수 있다는 사실과 관련 증상을 주의깊게 관찰하라는 정보가 반영되어 있으나\n- 식품의약품안전처는 전문가 및 환자에게 관련 정보를 환기시키고 주의사항을 당부하기 위해 안전성서한을 배포하며,\n- 추가로 국내외 허가현황 및 사용실태 등을 종합적으로 검토하여 허가사항 변경 등을 진행할 예정임\n□ 대상품목 현황\n❍ 가바펜틴 성분제제 - 한국화이자제약㈜, 뉴론틴캡슐100밀리그램 등 총 82개 업체 163품목\n❍ 프레가발린 성분제제 - 한국화이자제약㈜, 리리카캡슐75밀리그램 등 총 118개 업체 309품목\n\n￭ 조치사항\n□ 전문가를 위한 정보\n❍ 환자에게 이 약 복용 시 나타 날 수 있는 호흡기계 위험성을 알리고 다음과 같은 이상증상이 나타나는 경우 즉시 진료 받도록 안내하시기 바람\n- 방향감각 장애, 비정상적 어지럼증(현기증)\n- 극도의 졸림 또는 무기력증\n- 호흡 속도 저하, 얕은 호흡 또는 호흡 곤란\n- 정상적으로 대답‧반응하지 않거나 깨울 수 없는 상태\n- 입술‧손가락‧발가락의 색이 푸르스름하거나 옅음\n❍ 가바펜티노이드계 성분제제로 치료 시 최소 용량으로 시작하고 용량 증량 시 신중하시기 바람\n❍ 다음과 같이 호흡기계 위험요인을 가진 환자에 이 약 투여 시 호흡곤란, 진정 등의 증상을 관찰하시기 바람\n- 중추신경계억제제(오피오이드계 진통제, 항불안제, 항우울제, 항히스타민제 등) 병용투여\n- 폐기능 감소 상태, 고령자 등\n❍ 이 약 사용 시 나타나는 부작용은 한국의약품안전관리원에 보고하시기 바람\n□ 환자를 위한 정보\n❍ 상담 또는 진료 시 의료전문가에게 모든 복용약물, 알코올 섭취 등에 대해 알리시기 바람\n❍ 이 약을 복용하는 동안 다음 증상이 나타날 경우 즉시 진료를 받으시기 바람\n- 방향감각 장애, 비정상적 어지럼증(현기증)\n- 극도의 졸림 또는 무기력증\n- 호흡 속도 저하, 얕은 호흡 또는 호흡 곤란\n- 정상적으로 대답‧반응하지 않거나 깨울 수 없는 상태\n- 입술‧손가락‧발가락의 색이 푸르스름하거나 옅음\n❍ 이 약을 복용 중인 경우 임의로 중단하지 말고 의사, 약사와 상의하시기 바람\n❍ 이 약 사용 시 나타나는 부작용은 한국의약품안전관리원에 보고하시기 바람', 'admin', '2019-12-22 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=85&search_value=&search_term=&paging_value=4&setLine=&setHeader='),
	(17, '오셀타미비르, 자나미비르, 페라미비르 성분제제(식약처 의약품 안전성 서한)', '2020-01-15\n\n￭ 요약\n□ 배경\n❍ 인플루엔자 유행주의보 발령(`19.11.15) 이후 지속적으로 환자 발생이 증가함에 따라 독감치료제 처방·투여 시 주의사항을 안내하고자 함\n\n￭ 상세정보\n□ 주요 내용\n❍ 이 약을 투여중인 인플루엔자 환자들 중 주로 소아·청소년 환자에게서 경련, 섬망과 같은 신경정신계 이상반응이 나타날 수 있으며, 추락 등 사고에 이른 사례가 보고된 바 있음\n❍ 그러나 이 약 투여로 인한 것인지는 알려져 있지 않고 이 약을 투여하지 않았던 환자에서도 유사한 증상이 발현될 수 있음\n❍ 이와 관련하여 현재까지 약과의 인과관계는 확인되지 않았지만, 전문가와 국민들에게 주의사항을 당부하기 위해 안전성 서한을 배포함\n□ 대상품목 현황\n❍ (오셀타미비르) 69개 업체 250품목\n❍ (자나미비르) 1개 업체 1품목\n❍ (페라미비르) 1개 업체 1품목\n\n￭ 조치사항\n□ 의약전문가를 위한 정보\n❍ 이 약을 복용하는 소아, 청소년에 있어 만일의 사고를 방지하기 위하여 다음의 사항을 주의해주시기 바람\n- 이 약을 복용하는 인플루엔자 환자에게 이상행동의 발현 위험이 있음을 환자 및 보호자에게 알리시기 바람\n- 보호자는 치료제 투여와 관계없이 인플루엔자 환자를 적어도 2일간 혼자 있지 않도록 함께 하도록 안내하시기 바람\n- 또한, 인플루엔자 환자가 있는 경우 창문과 베란다, 현관문 등을 잠그며, 이상행동 발현에 대해 면밀히 관찰하도록 안내하시기 바람\n❍ 동 제제 사용 시 나타나는 부작용은 한국의약품안전관리원에 보고하시기 바람\n\n□ 환자 및 보호자를 위한 권고사항\n❍ 이 약과의 인과관계는 불분명 하지만 이 약의 복용 후 이상행동이 발현한 사례가 있음을 인지하시기 바람\n❍ 만일의 사고를 방지하기 위하여 보호자는 치료제 투여와 관계없이 소아·청소년 환자를 적어도 2일간 혼자 있지 않도록 함께 하시기 바람\n- 또한, 인플루엔자 환자가 있는 경우 창문과 베란다, 현관문 등을 꼭 잠그며, 이상행동 발현에 대해 면밀히 관찰하시기 바람\n❍ 임의로 이약의 복용을 중단하시 마시고, 복용하는 동안 이상 징후가 있으면 즉시 담당의사와 상의하시기 바람\n❍ 동 제제 사용 시 나타나는 부작용은 한국의약품안전관리원에 보고하시기 바람', 'admin', '2020-01-14 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=86&search_value=&search_term=&paging_value=4&setLine=&setHeader='),
	(18, '로카세린 성분제제(식약처 의약품 안전성 서한)', '2020-01-16\n\n￭ 요약\n□ 정보원\n❍ 미국 식품의약품청(FDA)은 향정신성 식욕억제제 ‘로카세린’ 성분제제의 임상시험 결과를 토대로 발암 가능성에 대하여 평가 중임을 발표함\n\n￭ 상세정보\n□ 주요내용\n❍ 미국 FDA는 ‘로카세린’ 성분제제의 심장질환 위험을 평가하는 임상시험 결과를 평가 중임\n- 5년간 약 12,000명을 대상으로 실시된 임상시험에서 위약 투여군에 비해 로카세린 투여군에서 더 많은 환자들이 암을 진단 받았음\n- 다만, 현재로서는 발암의 원인이 불확실하며, 로카세린 성분이 원인이라고 결론 내릴 수 없음\n- 미국 FDA는 임상시험 결과를 평가 중이며, 향후 검토가 완료되면 최종 결론 및 권고사항을 발표할 예정임\n❍ 이와 관련하여 식품의약품안전처는 의약전문가 및 환자에게 관련 정보를 제공하기 위하여 안전성서한을 배포하며\n- 추가로 국내외 허가현황 및 사용실태 등을 종합적으로 검토하여 허가사항 변경 등을 진행할 예정임\n\n□ 대상품목 현황\n❍ 일동제약(주), 벨빅정(로카세린염산염수화물) 및 벨빅엑스알정(로카세린염산염수화물) 2품목\n\n￭ 조치사항\n□ 전문가를 위한 정보\n❍ 로카세린 성분제제의 처방 및 치료 지속 여부 결정 시 유익성이 잠재적 위해성을 상회하는지 고려하시기 바람\n❍ 미국 FDA가 평가 중인 임상시험의 결과에 따르면\n- 시험대상자는 심혈관계 고위험군으로 연령은 64세(중앙값), 체중은 102kg(중앙값)이었으며\n- 약물투여 및 추적기간은 3.3년(중앙값)이었음\n- 전체 암 발생은 위약 투여군 5992명 중 210건(3.50%), 로카세린 투여군 5995명 중 215건(3.59%)이었으며\n- 위약 투여군에 비해 로카세린 투여군에서 전체 암, 유방관상피내암, 유방섬유선종의 발생률이 높았으나\n- 발암의 원인은 추가적인 평가가 필요함\n❍ 국내 ‘로카세린’ 성분제제의 허가사항에는 비임상시험 결과 일부 동물에서 암 발생률이 증가하였다는 정보가 반영되어 있음\n- 이는 1일 인체 임상용량을 수십배 상회하는 용량에서의 결과이고, 현재까지 인체와의 관련성은 알려지지 않았음\n❍ 식품의약품안전처는 국내외 허가현황, 사용실태 및 문헌자료 등을 종합 검토하여 필요한 경우 조치사항을 추가로 안내할 예정임\n\n□ 환자를 위한 정보\n❍ 상담 또는 진료 시 의료전문가에게 로카세린 성분제제의 잠재적 발암 위험성에 대해 상의하시기 바람\n❍ 이 약을 복용 중인 경우 임의로 중단하지 말고 의사, 약사와 상의하시기 바람\n❍ 이 약 사용 시 나타나는 부작용은 한국의약품안전관리원에 보고하시기 바람', 'admin', '2020-01-15 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=87&search_value=&search_term=&paging_value=4&setLine=&setHeader='),
	(19, '로카세린 성분제제(식약처 의약품 안전성 속보)', '2020-02-14\n\n￭ 요약\n□ 정보원\n❍ 미국 식품의약품청(FDA)은 향정신성 식욕억제제 ‘로카세린’ 성분제제의 암 발생 위험에 따른 조치내용을 발표함\n\n￭ 상세정보\n□ 주요내용\n❍ 미국 FDA는 ‘로카세린’ 성분제제의 제조사에 자발적인 시장 철수를 요청하였음\n- 이는 ‘로카세린’ 성분제제의 안전성 평가를 위한 임상시험 결과 위약 대비 암 발생 위험이 증가하는 것으로 나타났기 때문임\n❍ 이와 관련하여 식품의약품안전처는 국내 허가권자인 일동제약(주)에게 ‘로카세린’ 성분제제에 대한 판매중지 및 회수‧폐기를 권고하였음\n❍ 이에 식품의약품안전처는 의약전문가 및 환자에게 관련 정보를 제공하기 위하여 안전성 속보를 배포함\n□ 대상품목 현황\n❍ 일동제약(주), 벨빅정(로카세린염산염수화물) 및 벨빅엑스알정(로카세린염산염수화물) 2품목\n\n￭ 조치사항\n□ 전문가를 위한 정보\n❍ ‘로카세린’ 성분제제의 처방 및 조제를 중단하시기 바람\n❍ 현재 ‘로카세린’ 성분제제를 복용 중인 환자에게 암 발생 위험 정보와 복용중단을 안내하고 대체치료법을 논의하시기 바람\n❍ 해당 업체의 회수 조치에 적극 협조하여 주시기 바람\n❍ 미국 FDA는 ‘로카세린’ 성분제제를 복용한 환자에 대해 특별한 암 선별검사는 권장하지 않음\n❍ 미국 FDA의 임상시험 평가 결과에 따르면\n- 5년간 약 12,000명*을 대상으로 한 임상시험에서 위약 투여군에 비해 로카세린 투여군에서 더 많은 환자가 암을 진단 받았음\n* 과체중 또는 비만환자 중 심혈관계 고위험 환자\n- 로카세린 투여군 462명(7.7%)에서 520건, 위약 투여군 423명(7.1%)에서 470건의 원발암이 진단되었음\n- 위약 투여군에 비해 로카세린 투여군에서 췌장암, 대장암, 폐암 등 일부 암 종류의 발생률이 높았음\n- 로카세린 치료 기간이 증가할수록 위약 투여군 대비 암 발생률의 차이가 증가하였음\n□ 환자를 위한 권고사항\n❍ 로카세린 성분제제를 복용 중인 경우 복용을 중단하고 대체 치료법에 대하여 전문가와 상의하시기 바람', 'admin', '2020-02-13 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=88&search_value=&search_term=&paging_value=4&setLine=&setHeader='),
	(20, '울리프리스탈 성분제제(식약청 의약품 안전성 서한)', '2020-04-27\n\n￭ 요약\n❍ 유럽 약물감시위해평가위원회(PRAC)는 자궁근종 치료제인 ‘울리프리스탈’ 성분제제의 심각한 간손상 사례를 바탕으로 다음(주요내용)과 같은 임시조치를 권고함\n\n￭ 상세정보\n❍ 유럽 PRAC은 자궁근종 치료제인 ‘울리프리스탈’ 성분제제 복용 후 간이식이 필요한 심각한 간손상 사례가 보고되어 동 성분제제의 유익성-위해성을 평가하고 있음\n※ 동일 성분의 응급피임약은 간 손상 우려가 없으며 이번 조치의 대상이 아님\n❍ 유럽 PRAC은 평가가 진행되는 동안 다음의 임시조치를 권고함\n- 이 약으로 치료 중인 환자는 복용을 중단하고, 신규 환자에게 치료를 시작하지 말 것\n- 복용 중단 2~4주 후에도 간기능 검사를 실시할 것\n- 환자는 간 손상의 징후 및 증상*이 나타나면 즉시 의약전문가에게 알릴 것\n* 예: 구역, 구토, 우하륵부(오른쪽 갈비뼈 아래) 통증, 식욕부진, 피로감, 황달, 검은 소변\n- 평가가 완료되면 추가 정보 및 개정된 권고사항을 발표할 예정임\n❍ 이와 관련하여 식품의약품안전처는 의약전문가 및 환자에게 관련 정보를 제공하기 위하여 안전성서한을 배포함\n- 참고로, 국내에서 현재까지 간이식이 필요한 심각한 간 손상 사례는 보고된 바 없으며\n- 추가로 유럽의 평가 결과, 국내외 허가현황 및 사용실태 등을 종합 검토하여 필요한 경우 허가사항 변경 등을 진행할 예정임\n\n￭ 조치사항\n□ 전문가를 위한 정보\n❍ 자궁근종 치료제인 ‘울리프리스탈’ 성분제제의 처방 및 조제를 중단하시기 바람\n❍ 현재 ‘울리프리스탈’ 성분제제를 복용 중인 환자에게 복용중단을 안내하고 대체치료제를 논의하시기 바람\n❍ 환자들에게 간손상의 징후 및 증상이 나타나면 즉시 보고하도록 안내하시기 바람\n❍ 복용 중단 2~4주 후에도 간기능 검사를 실시하시기 바람\n❍ 식품의약품안전처는 유럽의 평가 결과, 국내외 허가현황, 사용실태 등을 종합 검토하여 필요한 경우 조치사항을 추가로 안내할 예정임\n\n□ 환자를 위한 정보\n❍ 자궁근종 치료를 위해 ‘울리프리스탈’ 성분제제를 복용 중인 경우 복용을 중단하고 대체치료법에 대하여 의약전문가와 상의하시기 바람\n❍ 간 손상 증상이 나타나면 즉시 의약전문가와 상의하시기 바람\n❍ 이 약 사용 시 나타나는 부작용은 한국의약품안전관리원에 보고하시기 바람', 'admin', '2020-04-26 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=89&search_value=&search_term=&paging_value=4&setLine=&setHeader='),
	(21, '㈜메디톡스 메디톡신주 등(식약처 의약품 안전성 속보)', '2020-04-17\n\n￭ 요약\n약    \nㅇ ㈜메디톡스社가 생산하는 메디톡신주(클로스트리디움 보툴리눔 독소 A형 (Hall 균주)) 50단위, 100단위, 150단위에 대해 잠정 제조·판매 중지 명령 및 사용중단을 요청함\nㅇ 업체명: ㈜메디톡스\nㅇ 품목명:　메디톡신주(클로스트리디움 보툴리눔 독소 A형(Hall 균주)) 50단위, 100단위, 150단위\n\n￭ 상세정보\n    ㅇ 식품의약품안전처는 ㈜메디톡스社의 메디톡신주 50단위, 100단위, 150단위에 대해 잠정 제조·판매중지를 명령하고 의료인등에게는 해당 품목의 사용중단을 요청함\nㅇ 이는 검찰이 2020.4.17.자로 무허 가 원액을 사용한 제품 생산, 원액 및 역가 정보 조작을 통한 국가출하승인 취득, 허가 내용 및 원액의 허용기준을 위반하여 제품을 제조·판매하는 등 약사법을 위반한 ㈜메디톡스와 대표를 기소한 것에 따른 후속조치임\nㅇ 식약처는 검찰로부터 관련자료를 제공받아 해당 품목 및 위반사항을 확인하고, 약사법 위반으로 메디톡신주 50단위, 100단위, 150단위에 대한 품목허가 취소 절차에 착수하였음\nㅇ 식약처는 이번 사건에서 확인된 원액의 기준 부적합이 국민 건강에 미치는 영향 등에 대하여는 중앙약사심의위원회 자문을 거쳐 안전성을 종합평가한 후 필요한 조치를 취할 예정임\nㅇ 안전성 평가 및 행정절차에 소요되는 기간을 감안하여 우선적으로 의료인 및 환자에게 해당 품목의 사용중단을 요청하고 주의사항을 안내해드리고자 안전성 속보를 배포함\n\n￭ 조치사항\n항    \nㅇ 해당 의약품은 약사법 위반으로 품목허가가 취소될 예정이므로 사용을 중단하고 다른 대체 품목을 투여하시기 바람\nㅇ 보툴리눔 제제 투여 시 소비자에게 동 조치대상 의약품과 관련된 정보사항에 대해 알리고, 불가피하게 사용하여야 할 경우 환자에게 동의를 구하시기 바람\nㅇ 일반적으로 보툴리눔 제제는 체내에 투여되는 양이 극소량이며, 일시적인 효과를 나타낸 후 체내에서 단백분해효소에 의해 분해되는 특징을 가짐을 이해하시기 바람\nㅇ 조치대상 의약품으로 인해 발생하는 부작용은 한국의약품안전관리원에 보고하시기 바람\nㅇ 해당 의약품을 투여받고 있는 경우 적절한 대체 품목에 대하여 의사와 상의하시기 바람\nㅇ 조치대상 의약품으로 인해 발생하는 부작용은 한국의약품안전관리원에 신고하시기 바람', 'admin', '2020-04-16 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=90&search_value=&search_term=&paging_value=4&setLine=&setHeader='),
	(22, '메트포르민 일부 의약품(식약처 의약품 안전성 속보)', '2020-05-26\n\n￭ 요약\n    ❍ 발사르탄, 라니티딘 등 의약품에서의 NDMA 검출로 인해 `19.11월부터 비의도적 불순물(NDMA 등) 안전관리 대책을 추진하고 있으며,\n- `19.12월 이후 해외 일부에서 메트포르민 의약품 NDMA 검출에 따른 회수조치 발표가 있어, 국내 제조에 사용 중인 원료의약품, 제조 및 수입완제의약품 수거·검사 등 조사 실시\n\n￭ 상세정보\n❍ 식약처는 국내 제조에 사용 중인 메트포르민 원료의약품 전체를 시험검사한 결과 NDMA가 잠정관리기준 이하로 확인되었으며,\n- 국내 유통 중인 288개 완제의약품 전체에 대한 시험검사 결과 31개 완제의약품에서 NDMA가 잠정관리기준*을 초과하여 검출됨을 확인\n* 0.041∼0.795ppm 검출(1일 최대복용량이 1,000mg인 경우 0.096ppm, 2,550mg인 경우 0.038ppm)\n- 해당 완제의약품 31개 품목에 대해 잠정 제조중지 및 판매중지 조치함\n- 이는 사전 예방적 차원의 잠정조치임\n\n￭ 조치사항\n□ 조치대상 완제의약품\n❍ 메트포르민 NDMA 초과 검출이 확인된 22개사 31개 완제의약품에 대해 잠정 제조중지 및 판매중지 조치함(붙임참고)\n□ 전문가를 위한 정보\n❍ NDMA 기준초과 검출된 제품으로 전문가들이 인체영향평가를 실시한 결과 추가로 암이 발생할 가능성은 매우 낮은 수준임\n❍ 조치대상 의약품을 복용하고 있는 환자에 대해 환자의 질병 상태를 고려하여 필요한 경우 다른 대체의약품으로 처방할 것을 권고함\n❍ 필요한 경우 환자에게 동 제제와 관련된 정보사항에 대해 알릴 것\n❍ 해당 의약품의 재처방·재조제 등 교환 원칙과 방법에 관련하여 상세한 사항은 식품의약품안전처 홈페이지(www.mfds.go.kr) 또는 보건복지부 홈페이지(www.mohw.go.kr)를 참고할 것\n□ 환자를 위한 정보\n❍ NDMA 기준초과 검출된 제품으로 전문가들이 인체영향평가를 실시한 결과 추가로 암이 발생할 가능성은 매우 낮은 수준이므로 환자들께서는 임의로 복용을 중단하지 말 것\n❍ 대체의약품으로의 변경은 담당 의사·약사와 반드시 상의하여 진행할 것\n❍ 동 제품 사용으로 나타나는 부작용은 한국의약품안전관리원으로 보고할 것\n❍ 참고로, 향후 동 사안에 대해 추가적으로 확인되는 국내·외 안전성 관련 정보는 지속적으로 제공될 것임\n❍ 해당 의약품의 재처방·재조제 등 교환 원칙과 방법에 관련하여 상세한 사항은 식품의약품안전처 홈페이지(www.mfds.go.kr) 또는 보건복지부 홈페이지(www.mohw.go.kr)를 참고할 것', 'admin', '2020-05-25 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=91&search_value=&search_term=&paging_value=4&setLine=&setHeader='),
	(23, '㈜메디톡스 메디톡신주 등(식약처 의약품 안전성 속보)', '2020-06-18\n\n￭ 요약\nㅇ ㈜메디톡스가 생산하는 메디톡신주(클로스트리디움보툴리눔독소A형) 등 3품목에 대한 품목허가 취소 등 정보를 알리고자 함\nㅇ 업체명: ㈜메디톡스\nㅇ 품목명: 메디톡신주(클로스트리디움 보툴리눔 독소 A형(Hall 균주)) 50단위, 100단위, 150단위\n\n￭ 상세정보\nㅇ 식품의약품안전처는 ㈜메디톡스의 메디톡신주(클로스트리디움보툴리눔독소A형) 등 3품목에 대해 2020.6.25.자로 품목허가를 취소함\nㅇ 이는 ㈜메디톡스가 무허가 원액을 사용한 제품 생산, 원액 및 역가 정보 조작을 통한 국가출하승인 취득, 허가 내용 및 원액의 허용기준을 위반하여 제품을 제조·판매하는 등 약사법을 위반했기 때문임\nㅇ 식약처는 법률 위반으로 품목허가 취소된 의약품이 국민에게 사용되지 않도록 ㈜메디톡스에 해당 유통 의약품을 회수·폐기토록 명령하였음\nㅇ 이에 해당 의약품을 보관 중인 의료기관 등에서는 업체의 회수에 적극 협조하여 주실 것을 당부드림\nㅇ 해당 의약품은 현재까지 사용현황과 보툴리눔 제제에 대한 국내외 임상논문, 일정기간 효과를 나타낸 후 체내에서 단백분해효소에 의해 분해되는 제제의 특성 등을 종합한 중앙약사심의위원회 자문 결과 안전성 우려는 크지 않은 것으로 의견이 모아짐\n\n￭ 조치사항\nㅇ 법률 위반으로 품목허가가 취소된 해당 의약품의 사용을 중단하고 다른 대체 품목을 투여하시기 바람\nㅇ 조치대상 의약품에 대한 업체의 회수조치에 적극 협조해주시기 바람\nㅇ 해당 의약품을 투여받고 있는 경우 적절한 대체 품목에 대하여 의사와 상의하시기 바람\nㅇ 조치대상 의약품으로 인해 발생한 부작용은 한국의약품안전관리원에 보고하시기 바람', 'admin', '2020-06-17 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=92&search_value=&search_term=&paging_value=4&setLine=&setHeader='),
	(24, '비스테로이드성 항염증제(NSAIDs)(식약처 의약품 안전성 서한)', '2020-10-17\n\n￭ 요약\n❍ 미국 식품의약품청(FDA)은 비스테로이드성 항염증제(NSAIDs)의 20주 이후 임부에게 사용하지 않도록 권고하는 서한을 발표함\n\n￭ 상세정보\n□ 주요내용\n❍ 미국 FDA는 임신 20주 전후에 비스테로이드성 항염증제(NSAIDs) 사용시 태아에게 드물지만 심각한 신장문제를 야기할 수 있음을 경고함\n- 진통 및 해열을 위해 흔히 사용되는 비스테로이드성 항염증제(NSAIDs) 제제는 태아를 둘러싸고 있는 양수량 감소 및 합병증을 초래할 수 있음\n- 임신 20주경 이후 태아의 신장은 대부분의 양수를 생성하기 때문에 신장문제는 양수량 감소를 초래할 수 있음\n- 이러한 안전성 문제는 특정 의학분과에는 알려져 있으나, 보건전문의료인 및 임부를 위해 권고사항을 더욱 널리 알리고자 함\n- 이번 정보는 전문가 처방하에 임신 중 임신 관련 특정 병태로 인해 81mg 저용량의 아스피린을 사용하는 경우에는 권고사항이 적용되지 않음\n❍ 이와 관련하여 식품의약품안전처는 의약전문가 및 환자에게 관련 정보를 제공하기 위하여 안전성서한을 배포하며,\n- 추가로 국내‧외 허가현황 및 사용실태 등을 종합적으로 검토하여 필요시 허가사항 변경 등을 진행할 예정임\n\n* 대상 품목 일부 정정하여 첨부합니다.', 'admin', '2020-10-16 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=93&search_value=&search_term=&paging_value=4&setLine=&setHeader='),
	(25, '메디톡스사, 메디톡신주 등(식약처 의약품 안전성 속보)', '2020-10-19\n\n￭ 요약\n㈜메디톡스社 메디톡신주 등에 대한 회수·폐기 및 잠정 제조·판매·사용 중지\n\n￭ 상세정보\n○ 식약처는 국가출하승인 대상인 보툴리눔 제제를 국가출하승인 받지 않거나 표시기재를 위반(한글표시 없음)하여 판매한 ㈜메디톡스社의 메디톡신주 50･100･150･200단위, 코어톡스주의 해당 제조번호에 대하여 회수·폐기를 명령함\n* 해당 품목 일부 제조단위\n○ 또한 국가출하승인 대상인 보툴리눔 제제를 국가출하승인 받지 않고 판매한 메디톡신주 50･100･150･200단위와 코어톡스주는 품목허가 취소절차에 착수하였으며\n- 동 품목에 대하여는 잠정 제조·판매 중지를 명령하고, 의료인에게는 해당 품목의 사용중단을 요청함\n* 해당 품목 전체 제조단위\n○ 아울러 의약품을 판매할 수 없는 자에게 의약품을 판매한 행위 등에 대해서도 약사법 위반사항에 대하여 행정처분 조치할 예정임\n○ 이에 식약처는 의약전문가 및 환자에게 관련 정보를 신속히 제공하기 위해서 안전성 속보를 배포함\n\n￭ 조치사항\n○ 식약처는 국가출하승인 대상인 보툴리눔 제제를 국가출하승인 받지 않거나 표시기재를 위반(한글표시 없음)하여 판매한 ㈜메디톡스社의 메디톡신주 50･100･150･200단위, 코어톡스주의 해당 제조번호에 대하여 회수·폐기를 명령함\n* 해당 품목 일부 제조단위\n○ 또한 국가출하승인 대상인 보툴리눔 제제를 국가출하승인 받지 않고 판매한 메디톡신주 50･100･150･200단위와 코어톡스주는 품목허가 취소절차에 착수하였으며\n- 동 품목에 대하여는 잠정 제조·판매 중지를 명령하고, 의료인에게는 해당 품목의 사용중단을 요청함\n* 해당 품목 전체 제조단위\n○ 아울러 의약품을 판매할 수 없는 자에게 의약품을 판매한 행위 등에 대해서도 약사법 위반사항에 대하여 행정처분 조치할 예정임\n○ 이에 식약처는 의약전문가 및 환자에게 관련 정보를 신속히 제공하기 위해서 안전성 속보를 배포함', 'admin', '2020-10-18 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=94&search_value=&search_term=&paging_value=4&setLine=&setHeader='),
	(26, '유니메드제약, 유니알주15mg(식약처 의약품 안전성 속보)', '2020-12-11\n\n￭ 요약\n유니메드제약㈜“ 유니알주15밀리그람(히알루론산나트륨)(수출명:유닐주)” 잠정 판매·사용 중지 및 회수 조치\n\n￭ 상세정보\n    백내장 수술의 보조요법제로 사용하는 유니메드제약㈜의 점안주사제 ‘유니알주15밀리그람(히알루론산나트륨)’ 일부 제조번호에서 품질(무균시험) 부적합이 확인되어 해당 제조번호 회수 및 판매·사용중지 조치하고,\n해당 품목의 제조‧출하를 잠정 중지시키고, 병·의원 등에 처방 제한을 요청\n\n￭ 조치사항\n품질(무균시험) 부적합이 확인된 2개 제조번호에 대해 회수 및 판매·사용중지 조치, 조사·검토 완료 시까지 해당 품목의 제조‧출하를 잠정 중지, 병·의원 등에 처방 제한 요청', 'admin', '2020-12-10 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=95&search_value=&search_term=&paging_value=4&setLine=&setHeader='),
	(27, '한약재 ‘세신(細辛)’ 분말(식약청 의약품 안전성 서한)', '2020-12-17\n\n￭ 요약\n    한약재 ‘세신(細辛)’ 분말 사용 제한\n\n￭ 상세정보\n❍ 독성시험 전문기관인 한국화학연구원 부설 안전성평가연구소에서 식품의약품안전처 의약품 등의 독성시험기준 및 OECD 국제 독성 가이드라인에 따라 세신에 대하여 실험동물 랫드 및 세포를 이용하여 독성시험하고 그 결과에 대한 위해성평가를 실시함\n❍ ‘세신’에 대한 동물(랫드)과 세포를 이용한 독성시험과 위해성평가 결과를 중앙약사심의워원회의 자문을 거쳐 ‘세신 분말’이 인체 위해 우려가 있는 것으로 판단\n- 다만, 분말에서 관찰된 독성은 뜨거운 물로 추출한 경우에는 나타나지 않음\n❍ 이와 관련하여 식품의약품안전처는 예방적 차원에서 세신 분말을 함유한 완제의약품에 대해 잠정 제조·판매중지 및 회수 조치함\n\n￭ 조치사항\n    - 세신 분말을 함유한 완제의약품에 대해 잠정 제조·판매중지 및 회수 조치함\n- 환·산제 등 분말 형태의 세신 함유 제제에 대해 처방·조제 중단을 권고함', 'admin', '2020-12-16 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=96&search_value=&search_term=&paging_value=4&setLine=&setHeader='),
	(28, '(주)메디톡스社, 이노톡스주(식약처 의약품 안전성 속보)', '2020-12-22\n\n￭ 요약\n    (주)메디톡스社, 이노톡스주 잠정 제조·판매·사용 중지 알림\n\n￭ 상세정보\n    ○(주)메디톡스社, 이노톡스주 잠정 제조·판매·사용 중지 알림\n- 이는 검찰이 2020.12.3.자로 안정성 시험 결과 조작을 통한 의약품 품목 허가 및 변경 허가 취득에 따른 약사법 위반으로 ㈜메디톡스 대표를 기소한 것에 따른 후속조치임\n- 식약처는 검찰로부터 인허가 범죄사실 통지를 받아 해당 품목 및 위반사항을 확인하였으며, 약사법 위반으로 이노톡스주에 대한 품목허가 취소 절차에 착수하였음\n- 행정절차에 소요되는 기간을 감안하여 우선적으로 의료인 및 환자에게 해당 품목의 사용중단을 요청하고 주의사항을 안내해드리고자 안전성 속보를 배포함\n\n￭ 조치사항\n○㈜메디톡스社가 생산하는 이노톡스주(클로스트리디움보툴리눔독소A형)에 대해 잠정 제조·판매 중지 명령 및 사용중단을 요청함\n○(전문가)보툴리눔 제제 투여 시 소비자에게 동 조치대상 의약품과 관련된 정보사항에 대해 알리고, 불가피하게 사용하여야 할 경우 환자에게 동의를 구하시기 바람\n○(소비자)해당 의약품을 투여받고 있는 경우 적절한 대체 품목에 대하여 의사와 상의하시기 바람\n○조치대상 의약품으로 인해 발생하는 부작용은 한국의약품안전관리원에 보고하시기 바람', 'admin', '2020-12-21 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=97&search_value=&search_term=&paging_value=3&setLine=&setHeader='),
	(29, '유니메드제약㈜ 주사제(식약처 의약품 안전성 속보)', '2020-12-24\n\n￭ 요약\n    유니메드제약㈜ 주사제 4품목 추가 잠정 판매·사용 중지 및 무균조작 주사제 3품목 전 제조번호 회수\n\n￭ 상세정보\n식품의약품안전처는 유니메드제약㈜에서 제조한 ‘유니알주15밀리그람(히알루론산나트륨)’에 대한 12.11일 조치 이후 해당 제조소를 점검한 결과, 해당 제조소의 주사제 제조과정과 제조시설 전반에 걸쳐 미생물 오염 가능성이 있음을 확인, 해당 제조시설에서 생산되는 모든 주사제 제품에 대해 잠정 제조·판매 중지 명령하고, 무균조작 주사제 3품목은 전 제조번호 회수 조치함\n\n❍ 잠정 제조판매 중지 및 전 제조번호 회수\n\n- 유니메드제약㈜ 히알론디스포주(히알루론산나트륨)(프리필드)\n\n- 유니메드제약㈜ 유닐론디스포주(히알루론산나트륨)(프리필드)\n\n- 유니메드제약㈜ 유니알주15밀리그람(히알루론산나트륨)\n\n※ 유니알주15밀리그람(히알루론산나트륨) 잠정 제조‧판매 중지는 기 조치(12.11)\n\n❍ 잠정 제조‧판매 중지\n\n- 유니메드제약㈜ 유니본주(이반드론산나트륨수화물)\n\n- 유유제약㈜ 마빌큐주(이반드론산나트륨수화물)\n\n￭ 조치사항\n    해당 제조시설에서 생산되는 모든 주사제 제품에 대해 잠정 제조·판매 중지 명령하고, 무균조작 주사제 3품목은 전 제조번호 회수 조치', 'admin', '2020-12-23 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=98&search_value=&search_term=&paging_value=3&setLine=&setHeader='),
	(30, '(주)메디톡스社, 이노톡스주(식약처 의약품 안전성 속보)', '2021-01-18\n\n￭ 요약\n    ㈜메디톡스社 이노톡스주 품목허가 취소 및 회수·폐기 조치 알림\n\n￭ 상세정보\n○㈜메디톡스社가 생산하는 이노톡스주(클로스트리디움보툴리눔독소A형)의 품목허가를 취소하고 회수폐기 조치 알림\n-안정성 시험 결과 조작을 통하여 의약품 품목허가 및 변경 허가를 취득한 ㈜메디톡스社 이노톡스주에 대해 \'21.1.26.자로 품목허가를 취소함\n-식약처는 지난 \'20.12.22.자 안전성 속보를 통해 잠정 제조·판매·사용 중지를 알리고 품목허가 취소 절차에 착수함을 알렸으며, 이번 조치는 그에 따른 후속조치임\n-해당 의약품을 보관 중인 의료기관 등에서는 업체의 회수에 적극 협조하여 주실 것을 당부드림\n\n￭ 조치사항\n    ○(전문가)품목허가가 취소된 해당 의약품의 사용을 중단하고 대체 품목을 투여하시기 바람\n○(소비자)해당 의약품을 투여받고 있는 경우 적절한 대체 품목에 대하여 의사와 상의하시기 바람\n○조치대상 의약품으로 인해 발생하는 부작용은 한국의약품안전관리원에 보고하시기 바람', 'admin', '2021-01-17 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=99&search_value=&search_term=&paging_value=3&setLine=&setHeader='),
	(31, '㈜바이넥스‘닥스펜정(덱시부프로펜)’등 6품목 잠정 제조·판매·사용 중지(식약처 의약품 안전성 속보)', '2021-03-08\n\n￭ 요약\n㈜바이넥스社가 생산하는 닥스펜정(덱시부프로펜), 로프신정250mg(시프로플록사신염산염수화물), 셀렉틴캡슐(플루옥세틴염산염), 셀렉틴캡슐10밀리그램(플루옥세틴염산염), 아모린정(글리메피리드), 카딜정1밀리그램(독사조신메실산염) 등 6개 품목에 대해 잠정 제조·판매 중지 명령 및 사용중단을 요청함\n\n￭ 상세정보\n□ 주요내용\n❍ 식품의약품안전처는 ㈜바이넥스社에서 허가 또는 신고된 사항과 다르게 제조한 닥스펜정 등 6개 품목에 대한 회수계획을 부산지방식품의약품안전청에 제출함에 따라, 사전 예방적 차원에서 해당 제품에 대해 잠정 제조·판매 중지를 명령하고, 6품목 전 제조번호에 대해 회수 조치함\n❍ 의ㆍ약 관계자 여러분께서는 해당 품목의 사용을 중지하여 주시고 유통품 회수가 적절히 수행될 수 있도록 적극 협조하여 주시기 바라며,\n❍ 의·약전문가는 동 정보사항에 유의하여 해당 제품의 처방 및 사용을 중지하고 대체의약품을 사용할 것을 당부드림\n\n□ 조치대상 의약품\n❍ 잠정 제조판매 중지 및 전 제조번호 회수\n- 닥스펜정(덱시부프로펜)\n- 로프신정250mg(시프로플록사신염산염수화물)\n- 셀렉틴캡슐(플루옥세틴염산염)\n- 셀렉틴캡슐10밀리그램(플루옥세틴염산염)\n- 아모린정(글리메피리드)\n- 카딜정1밀리그램(독사조신메실산염)\n\n￭ 조치사항\n㈜바이넥스社가 생산하는 닥스펜정 등 6개 품목에 대해 잠정 제조·판매 중지 및 회수조치', 'admin', '2021-03-07 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=100&search_value=&search_term=&paging_value=3&setLine=&setHeader='),
	(32, '㈜바이넥스 수탁제조 `덱펜정(덱시부프로펜)`등 32품목 잠정 제조·판매·사용 중지(식약처 의약품 안전성 속보)', '2021-03-09\n\n￭ 요약\n㈜바이넥스社가 수탁제조하는 다른 의약품 제조업체의 32개 품목에 대해 잠정 제조·판매 중지 명령 및 사용중단을 요청함\n\n￭ 상세정보\n❍ 식품의약품안전처는 ㈜바이넥스社에서 제조한 닥스펜정 등 6개 품목에 대한 3.8일 조치(잠정 제조‧판매 및 사용중지) 이후 해당 제조소에 대한 현장 조사 결과, 기 조치한 품목과 동일한 방법으로 수탁제조하고 있는 다른 의약품 제조업체의 32개 품목을 확인하여 사전 예방적 차원에서 해당 제품에 대해 잠정 제조·판매 중지를 명령하고, 32개 품목의 전 제조번호에 대해 회수 조치함\n❍ 의ㆍ약 관계자 여러분께서는 해당 품목의 사용을 중지하여 주시고 유통품 회수가 적절히 수행될 수 있도록 적극 협조하여 주시기 바라며,\n❍ 의·약전문가는 동 정보사항에 유의하여 해당 제품의 처방 및 사용을 중지하고 대체의약품을 사용할 것을 당부드림\n\n￭ 조치사항\n㈜바이넥스社에서 제조한 닥스펜정 등과 동일한 방법으로 수탁제조하는 24개사 32개 품목에 대해 잠정 제조‧판매 중지 및 전 제조번호 회수', 'admin', '2021-03-08 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=101&search_value=&search_term=&paging_value=3&setLine=&setHeader='),
	(33, '㈜비보존제약 제조(수탁 포함) `디스트린캡슐(디아세레인)` 등 9개 품목 잠정 제조·판매·사용 중지(식약처 의약품 안전성 속보)', '2021-03-12\n\n￭ 요약\n㈜비보존제약에서 제조하는 의약품 4개 품목과 동 업체에서 수탁 제조하는 의약품 5개 품목에 대해 잠정 제조·판매 중지 명령 및 사용중단을 요청함\n\n￭ 상세정보\n❍ 식품의약품안전처는 ㈜비보존제약에서 제조(수탁 포함)한 디스트린캡슐(디아세레인) 등 4개 품목 관련 해당 제조소에 대한 현장 조사 결과 허가 또는 신고된 사항과 다르게 제조하고 있는 것을 확인하였으며, 해당 품목과 동일한 방법으로 수탁제조하고 있는 다른 의약품 제조업체의 5개 품목을 확인하여 사전 예방적 차원에서 해당 9개 품목에 대해 잠정 제조·판매 중지를 명령하고, 전 제조번호에 대해 회수 조치함\n❍ 의ㆍ약 관계자 여러분께서는 해당 품목의 사용을 중지하여 주시고 유통품 회수가 적절히 수행될 수 있도록 적극 협조하여 주시기 바라며,\n❍ 의·약전문가는 동 정보사항에 유의하여 해당 제품의 처방 및 사용을 중지하고 대체의약품을 사용할 것을 당부드림\n\n￭ 조치사항\n㈜비보존제약에서 제조한 디스트린캡슐(디아세레인) 등 4개 품목 및 이와 동일한 방법으로 수탁제조하는 5개사 5개 품목에 대해 잠정 제조‧판매 중지 및 전 제조번호 회수', 'admin', '2021-03-11 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=102&search_value=&search_term=&paging_value=3&setLine=&setHeader='),
	(34, '아스트라제네카 코로나19 백신(식약처 의약품 안전성 서한)', '2021-03-19\n\n￭ 요약\n    유럽 의약품청(EMA)의 약물감시 위해평가 위원회(PRAC)는 아스트라제네카 코로나19 백신이 혈소판감소를 동반한 희귀한 형태의 혈액 응고와의 잠재적 연관성에도 불구하고 유익성이 위험성을 여전히 상회한다고 발표함\n\n￭ 상세정보\nㆍ 코로나19가 만연한 상황(코로나19 자체가 응고 문제를 초래할 수 있으며 치명적일 수 있음)에서 백신의 유익성이 위험성을 상회함\nㆍ 백신 접종이 혈액 응고(혈전색전)의 전반적인 위험 증가와 관련이 없음\nㆍ 백신의 특정 배치 또는 특정 제조시설과 관련 문제가 있다는 증거는 없음\nㆍ 백신은 혈소판감소증이 동반된 혈액 응고의 매우 드문 사례(뇌정맥동혈전증 포함)와 관련 있을 가능성이 있음', 'admin', '2021-03-18 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=103&search_value=&search_term=&paging_value=3&setLine=&setHeader='),
	(35, '아스트라제네카 코로나19 백신(식약처 의약품 안전성 서한)', '2021-04-08\n\n￭ 요약\n유럽의약품청(EMA)의 약물감시 위해평가 위원회(PRAC)는 아스트라제네카 코로나19 백신과 혈소판감소를 동반한 흔치 않은 혈액 응고와 잠재적 연관성을 확인했다고 발표함\n\n￭ 상세정보\nㆍ EMA의 약물감시 위해평가 위원회(PRAC)는 혈소판감소를 동반한 흔치 않은 혈액 응고가 아스트라네제카 코로나19 백신의 매운 드문 부작용으로 포함돼야 한다고 발표함\nㆍ 혈소판감소가 동반된 흔치 않은 혈액 응고 보고 사례 대부분은 60세 미만 여성에서 백신 접종 후 2주 이내에 발생했음\nㆍ 혈전은 뇌정맥(뇌정맥동혈전증, CVST), 복부정맥(내장정맥혈전증, splanchnic vein thrombosis) 및 동맥에서 혈소판감소증과 함께 발생하며 때로는 출혈을 동반함\nㆍ 혈소판감소를 동반한 혈액 응고는 매우 드물게 보고되었으며, 코로나19를 예방하는 동 백신의 전반적인 유익성은 부작용의 위험성을 상회함', 'admin', '2021-04-07 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=104&search_value=&search_term=&paging_value=3&setLine=&setHeader='),
	(36, '종근당 제조 6개품목 잠정 제조판매사용 중지(식약처 의약품 안전성 속보)', '2021-04-21\n\n￭ 요약\n㈜종근당에서 제조하는 의약품 4개 품목과 동 업체에서 수탁 제조하는 의약품 2개 품목에 대해 잠정 제조·판매 중지 명령 및 사용 중단을 요청\n\n￭ 상세정보\n식품의약품안전처는 ㈜종근당에서 제조(수탁 포함)한 리피로우정10mg(무수아토르바스타틴칼슘) 등 6개 품목 관련 해당 제조소에 대한 현장 조사 결과 허가 또는 신고된 사항 등과 다르게 제조하고 있는 것을 확인하였으며, 사전 예방적 차원에서 해당 6개 품목에 대하여 잠정\n제조·판매 중지를 명령하고, 전 제조번호에 대해 회수 조치함\n\n￭ 조치사항\n㈜종근당에서 제조한 리피로우정10mg(무수아토르바스타틴칼슘) 등 4개 품목 및 동 업체에서 수탁 제조하는 2개사 2개 품목에 대해 잠정 제조‧판매 중지 및 전 제조번호 회수', 'admin', '2021-04-20 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=105&search_value=&search_term=&paging_value=3&setLine=&setHeader='),
	(37, '한약재 위령선 회수·폐기 알림(식약처 의약품 안전성 서한)', '2021-05-04\n\n￭ 요약\n    \n한약재 \'위령선\' 회수·폐기 알림 및 사용주의 안내\n\n￭ 상세정보\n❍ 서울특별시보건환경연구원에서 시중 유통 한약규격품 중 위령선 수거검사 결과 엔탭허브위령선(제조번호: ETC20561-1), 현진위령선(제조번호: 20135-01), 경희한약위령선(제조번호: CLE-1901)에서 검정색으로 염색된 이물(현재 맥문동 뿌리로 추정)이 혼입되었음을 확인하였으며, 이는 기원이 다른 식물을 위령선으로 보이게 하기 위해 검은색으로 염색한 것으로 추정\n❍ 우리 처에서는 해당 품목에 대한 회수·폐기 및 행정처분 등의 조치와 함께 정확한 원인파악을 위해 해당 한약재 제조업체에 대한 현장조사와 유통 중인 다른 위령선(규격품)에 대한 수거·검사를 진행하고 있음\n❍ 사용자께서는 해당 제조번호의 한약규격품 ‘위령선’은 사용을 중지하여 주시고 유통품 회수가 적절히 수행될 수 있도록 적극 협조하여 주실 것을 당부드림\n❍ 현재 이물에 대한 정확한 정보가 없으나, 위령선을 물에 넣어 휘저었을 때 즉시 색소가 물에 용해되어 육안으로 확인할 수 있으므로 사용 전에 확인한 후 사용할 수 있도록 주의 당부드림\n\n￭ 조치사항\n❍ 3개사 3개 한약규격품에 대한 회수·폐기 및 행정처분 등의 조치, 해당 한약재 제조업체에 대한 현장조사\n❍ 유통 중인 다른 위령선(규격품)에 대한 수거·검사', 'admin', '2021-05-03 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=106&search_value=&search_term=&paging_value=3&setLine=&setHeader='),
	(38, '한올바이오파마㈜ 관련 6개품목 잠정 제조판매사용 중지(식약처 의약품 안전성 속보)', '2021-05-11\n\n￭ 요약\n    \n한올바이오파마㈜에서 수탁 제조한 의약품 6개 품목에 대해 잠정 제조·판매 중지 명령 및 사용중단을 요청함\n\n￭ 상세정보\n식품의약품안전처는 한올바이오파마㈜와 위‧수탁 제조 계약을 맺은 6개사 6개 품목에 대해 현장 점검 등을 통하여 최종 확인한 결과, 6개 품목에 대한 허가 또는 변경 허가를 받는 과정에서 제출된 안정성 시험 자료가 조작되었음을 확인하여, 약사법 위반으로 품목허가 취소 절차에 착수하고, 잠정 제조·판매 중지 및 회수 조치를 결정함. 참고로, 문제 되는 안정성 시험 자료는 수탁사인 한올바이오파마㈜에서 조작한 것임을 확인함\n\n￭ 조치사항\n한올바이오파마㈜에서 수탁 제조한 삼성이트라코나졸정 등 6개사 6개 품목에 대해 잠정 제조‧판매 중지 및 전 제조번호 회수', 'admin', '2021-05-10 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=107&search_value=&search_term=&paging_value=3&setLine=&setHeader='),
	(39, '㈜종근당 ‘이모튼캡슐’ 효능·효과 범위 축소 예정(식약처 의약품 안전성 서한)', '2021-05-21\n\n￭ 요약\n    \n‘(주)종근당’ 제조품목 ‘이모튼캡슐(아보카도-소야 불검화물의 추출물)’의 효능·효과를 ‘▲치주질환(치조농루)에 의한 출혈 및 통증의 보조요법 ▲골관절염(퇴행골관절염)’에서 ‘성인 무릎 골관절염의 증상완화’로 축소하는 내용의 허가사항 변경 조치 예정\n\n￭ 상세정보\n‘이모튼캡슐(프랑스 제품명: Piascledine)’의 원개발국인 프랑스에서 해당 제품의 허가사항 중 효능·효과가 ‘성인 무릎 골관절염의 증상완화’로 변경되었음이 확인됨에 따라, 국내 해당 제품의 효능·효과도 이와 동일하게 변경하고자 허가사항 변경 절차가 진행 중에 있음(최종 변경은 행정절차를 거쳐 7월 중 예정)', 'admin', '2021-05-20 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=108&search_value=&search_term=&paging_value=3&setLine=&setHeader='),
	(40, '동인당제약(주) 제조 13개품목 잠정 제조판매사용 중지(식약처 의약품 안전성 속보)', '2021-05-25\n\n￭ 요약\n동인당제약㈜에서 제조하는 의약품 12개 품목과 동 업체에서 수탁 제조하는 의약품 1개 품목에 대해 잠정 제조·판매 중지 명령 및 사용중단을 요청\n\n￭ 상세정보\n    \n식품의약품안전처는 동인당제약㈜에서 제조(수탁 포함)한 ‘로바스과립’ 등 의약품 13개 품목 관련 해당 제조소에 대한 현장 조사 결과, 허가 또는 신고된 사항 등과 다르게 제조하고 있는 것을 확인하였으며, 사전 예방적 차원에서 해당 13개 품목에 대하여 잠정 제조·판매 중지를 명령하고, 전 제조번호에 대해 회수 조치함\n\n￭ 조치사항\n    \n동인당제약㈜에서 제조한 ‘로바스과립’ 등 12개 품목 및 동 업체에서 수탁 제조하는 1개사 1개 품목에 대해 잠정 제조·판매 중지 및 전 제조번호 회수', 'admin', '2021-05-24 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=109&search_value=&search_term=&paging_value=3&setLine=&setHeader='),
	(41, '한솔신약(주) 제조 3개 품목 잠정 제조·판매·회수·폐기 명령(식약처 의약품 안전성 속보)', '2021-06-02\n\n￭ 요약\n한솔신약(주)에서 제조하는 의약품 3개 품목에 대해 잠정 제조·판매 중지 명령 및 회수·폐기 조치함\n\n￭ 상세정보\n식품의약품안전처는 한솔신약(주)에서 제조한 ‘근골환(양혈장근건보환)’ 등 의약품 3개 품목 관련 해당 제조소에 대한 현장 조사 결과, 신고된 사항 등과 다르게 제조하고 있는 것을 확인하였으며,\n사전예방적 차원에서 해당 3개 품목에 대하여 잠정 제조·판매 중지를 명령하고, 전 제조번호에 대해 회수 조치함\n\n￭ 조치사항\n한솔신약(주)에서 제조한 ‘근골환(양혈장근건보환)’ 등 3개 품목에 대해 잠정 제조·판매 중지 및 전 제조번호 회수', 'admin', '2021-06-01 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=110&search_value=&search_term=&paging_value=3&setLine=&setHeader='),
	(42, '모세혈관 누출 증후군 병력이 있는 경우 아스트라제네카 코로나19 백신을 접종하지 말 것을 권고(식약처 의약품 안전성 서한)', '2021-06-14\n\n￭ 요약\n유럽 의약품청(EMA)의 약물감시 위해평가 위원회(PRAC)는 모세혈관 누출 증후군 병력이 있는 사람들은 아스트라제네카 코로나19 백신을 접종하지 말 것을 권고함\n\n￭ 상세정보\n❍ EMA의 약물감시 위해평가 위원회(PRAC)는 모세혈관 누출 증후군이 아스트라제네카 코로나19 백신의 새로운 부작용으로 추가되어야 한다고 발표함\n❍ PRAC는 아스트라제네카 코로나19 백신을 접종 받은 사람들에서 나타난 모세혈관 누출 증후군 6건을 검토하였음\n- 대부분은 여성에게 백신 접종 후 4일 이내 발생했으며, 3건은 모세혈관 누출 증후군의 병력이 있었고, 이 중 1건은 치명적이었음\n* ‘21.5.27. 유럽경제지역(EEA) 및 영국(UK)에서 동 백신이 7,800만회 이상 도즈가 투여되었음\n❍ 모세혈관 누출 증후군은 매우 드물고 심각한 질환으로 작은혈관(모세혈관)에서 체액 누출을 유발해 팔과 다리의 부종, 저혈압, 혈액농축 및 저알부민혈증(중요한 혈액 단백질)을 야기함\n❍ 보건의료전문가들은 모세혈관 누출 증후군의 징후와 증상 및 이전에 해당 증상으로 진단 받았던 사람들에서 재발 위험성에 대해 인지하고 있어야 함', 'admin', '2021-06-13 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=111&search_value=&search_term=&paging_value=3&setLine=&setHeader='),
	(43, 'mRNA 코로나19 백신 접종 후 심근염 및 심막염 보고 사례 관련 안전성 정보(식약처 의약품 안전성 서한)', '2021-06-25\n\n￭ 요약\n미국 질병통제예방센터(CDC)는 mRNA 코로나19 백신 접종 후 심근염 및 심막염 보고 사례 관련 안전성 정보를 발표함\n\n￭ 상세정보\n❍ CDC는 미국에서 mRNA 코로나 19 백신 접종 후 심근염 및 심막염 사례가 보고되었다고 발표함\n- 이들 사례는 드물게 보고되었으며, mRNA 코로나19 백신 접종 후 특히 청소년 및 젊은 성인에서 보고됨\n❍ 확인 사례에서 대부분은 16세 이상 남성 청소년 및 젊은 성인에서 발생하였음\n- 1회차 접종보다 2회차 접종 후 더 자주 발생했으며, 일반적으로 백신 접종 후 며칠 이내 발생하였음\n❍ 치료를 받은 대부분의 환자들은 치료에 양호한 반응을 보였고 빠르게 회복하였음\n❍ 코로나19 감염 및 관련 중증 합병증 위험을 고려시 코로나19를 예방하는 백신의 전반적인 유익성이 위험성을 상회함\n❍ mRNA 코로나19 백신을 접종 후 7일 이내 다음 증상이 나타난다면 즉시 진료를 받으시기 바람\n- 가슴통증, 숨참\n- 빠른 심박동, 심장이 빠르게 뜀(심장 조동) 또는 두근거림', 'admin', '2021-06-24 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=112&search_value=&search_term=&paging_value=2&setLine=&setHeader='),
	(44, '삼성제약(주) 제조 6개품목 잠정 제조판매사용 중지', '2021-07-08\n\n￭ 요약\n삼성제약㈜에서 제조하는 의약품 5개 품목과 동 업체에서 수탁 제조하는 의약품 1개 품목에 대해 잠정 제조·판매 중지 명령 및 사용중단을 요청함\n\n￭ 상세정보\n식품의약품안전처는 삼성제약㈜에서 제조(수탁 포함)한 ‘게라민주’ 등 의약품 6개 품목 관련 해당 제조소에 대한 현장 조사 결과, 허가 또는 신고된 사항 등과 다르게 제조하고 있는 것을 확인하였으며, 사전 예방적 차원에서 해당 6개 품목에 대하여 잠정 제조·판매 중지를 명령하고, 전 제조번호에 대해 회수 조치함\n\n￭ 조치사항\n삼성제약㈜에서 제조한 ‘게라민주’ 등 5개 품목 및 동 업체에서 수탁 제조하는 1개사 1개 품목에 대해 잠정 제조·판매 중지 명령 및 전 제조번호 회수', 'admin', '2021-07-07 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=113&search_value=&search_term=&paging_value=2&setLine=&setHeader='),
	(45, '혈장증량제 “히드록시에틸전분(HES)” 성분 제제 안전성 허가사항 변경지시 (식약처 의약품 안전성 서한)', '2021-07-08\n\n￭ 요약\n의약품 안전성 서한 배포 알림[히드록시에틸전분(HES) 성분 제제]\n\n￭ 상세정보\n1. 미국 식품의약품청(FDA)에서 혈장증량제로 사용되는 "히드록시에틸전분(HES)" 성분 제제에 대한 안전성 정보를 발표함에 따라 우리 처에서는 국내 의약전문가 및 소비자 단체 등에 붙임과 같이 안전성 서한을 배포하니,\n\n2. 관련 단체(협회) 등에서는 회원사 및 비회원사에 동 정보사항을 널리 전파해 주시기 바라며, 의약품등 사전·사후 관리 관련 기관(부서) 등에서는 관련 업무에 참고하시기 바랍니다.\n\n￭ 조치사항\n의약품 안전성 서한 배포 알림', 'admin', '2021-07-07 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=114&search_value=&search_term=&paging_value=2&setLine=&setHeader='),
	(46, '금연치료보조제(바레니클린) 사용 시 주의 권고(식약처 의약품 안전성 서한)', '2021-07-09\n\n￭ 요약\n해외의 금연치료보조제(바레니클린) 중 불순물(N-nitroso-varenicline) 검출과 관련된 회수가 진행됨에 따라,\n- 식약처의 안전성 조사 진행 상황을 알리고, 그 간의 수집 정보 등을 종합 검토하여 사전예방적 차원에서 국내 전문가 및 소비자에게 필요한 조치 등을 권고\n\n￭ 상세정보\n❍ 금연치료보조제에 대한 불순물 안전관리를 위하여 `바레니클린` 함유 완제의약품* 제조 수입자에게 시중 유통 가능한 유효기간 내 모든 제조번호에 대하여 불순물인 N-nitroso-varenicline 시험검사 후 결과를 제출하도록 하였음\n* 챔픽스정(바레니클린타르타르산염 0.5mg, 1mg) 및 염 변경(바레니클린살리실산염, 바레니클린푸마르산염) 국내 제조 의약품\n❍ 현재까지 국내 유통 중인 `바레니클린` 함유 완제의약품에 대한 불순물 시험결과가 확인되지는 않았지만,\n- 캐나다에 이어 최근 미국에서 `바레니클린` 함유 일부 완제의약품에 대한 회수 발표가 있고,\n- 국내 유통 중인 완제의약품 시험검사에 상당기간 소요되고,\n- 국내 제품에서 불순물의 발생가능성을 배제할 수 없는 점 등을 고려하여 사용시 주의를 기하도록 권고함\n\n￭ 조치사항\n금연치료보조제(바레니클린) 사용 시 주의를 기하도록 권고', 'admin', '2021-07-08 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=115&search_value=&search_term=&paging_value=2&setLine=&setHeader='),
	(47, '모세혈관 누출 증후군 병력이 있는 경우 얀센 코로나19 백신을 접종하지 말 것을 권고함 (식약처 의약품 안전성 서한)', '2021-07-12\n\n￭ 요약\n유럽 의약품청(EMA)의 약물감시 위해평가 위원회(PRAC)는 모세혈관 누출 증후군 병력이 있는 사람들은 얀센 코로나19 백신을 접종하지 말 것을 권고함\n\n￭ 상세정보\n❍ EMA의 약물감시 위해평가 위원회(PRAC)는 모세혈관 누출 증후군이 얀센 코로나19 백신의 새로운 부작용으로 추가되어야 한다고 발표함\n❍ PRAC는 얀센 코로나19 백신을 접종 받은 사람들에서 나타난 모세혈관 누출 증후군 3건을 검토하였음\n- 백신 접종 후 2일 이내에 발생하였으며, 1건은 모세혈관 누출 증후군의 병력이 있었고, 2건은 치명적이었음\n* ‘21.6.21. 기준 전 세계적으로 동 백신이 1,800만회 이상 도즈가 투여되었음\n❍ 모세혈관 누출 증후군은 매우 드물고 심각한 질환으로 작은혈관(모세혈관)에서 체액 누출을 유발해 주로 팔과 다리의 부종, 저혈압, 혈액농축 및 저알부민혈증(중요한 혈액 단백질)을 야기함\n❍ 보건의료전문가들은 모세혈관 누출 증후군의 징후와 증상 및 이전에 해당 증상으로 진단 받았던 사람들에서 재발 위험성에 대해 인지하고 있어야 함\n❍ 보건의료전문가들은 모세혈관 누출 증후군의 징후 및 증상에 주의를 기울여 백신 접종 후 급성 모세혈관 누출 증후군이 있는 환자들을 신속하게 치료할 수 있도록 해야 함\n❍ 백신을 접종받는 사람들에게 접종 며칠 후 다음 증상이 나타나면 즉시 진료를 받도록 알려주시기 바람. 이는 저혈압으로 인한 어지러움과 관련이 있을 수 있음\n- 급격한 팔, 다리의 부종\n- 갑작스러운 체중증가\n❍ 얀센 코로나19 백신 접종 며칠 후 다음 증상이 나타난다면 즉시 치료를 받으시기 바람. 이는 저혈압으로 인한 어지러움이 함께 발생할 수도 있음\n- 급격한 팔, 다리의 부종\n- 갑작스러운 체중증가', 'admin', '2021-07-11 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=116&search_value=&search_term=&paging_value=2&setLine=&setHeader='),
	(48, '아스트라제네카 코로나19 백신과 얀센 코로나19 백신 접종 후 길랭-바레 증후군 보고 사례 관련 안전성 정보', '2021-07-27\n\n￭ 요약\nWHO, 국외 의약품 규제기관에서 아스트라제네카 코로나19 백신과 얀센 코로나19 백신 접종 후 길랭-바레 증후군 보고 사례 관련 안전성 정보를 발표함\n\n￭ 상세정보\n❍ WHO 및 국외 의약품 규제기관에서 아스트라제네카 코로나19 백신과 얀센 코로나19 백신 접종 후 길랭-바레 증후군 사례가 매우 드물게 보고되어 주의할 것을 발표함\n* (유럽) 아스트라제네카 백신 허가사항 변경(‘21.7.19.)\n(유럽) 얀센 백신 허가사항 변경(‘21.7.22.)\n(미국) 얀센 백신 긴급사용승인 정보 변경(‘21.7.12.)\n❍ 길랭-바레 증후군은 드문 신경학적 장애로 인체의 면역체계가 신경세포를 손상시켜 통증, 무감각, 근력 약화 등을 초래하고 심한 경우 마비를 일으킬 수 있음\n- 대부분의 사람들은 해당 장애로부터 회복되었음\n< 아스트라제네카 코로나19 백신 >\n❍ EMA의 약물감시위해평가위원회(PRAC)는 아스트라제네카 코로나19 백신 접종 후 보고된 길랭-바레 증후군 227건에 대해 검토하였음\n* ‘21.6.27. 기준 유럽경제지역(EU/EEA)에서 보고, 이 지역에서 동 백신이 5,140만회 이상 도즈가 투여됨\n< 얀센 코로나19 백신 >\n❍ FDA는 미국에서 얀센 코로나19 백신 접종 후 길랭-바레 증후군이 100건 보고되었으며, 이 중 1건은 치명적이었음\n* ‘21.7.12. 기준 미국 내 동 백신이 1,250만회 이상 도즈가 투여됨\n❍ EMA의 약물감시위해평가위원회(PRAC)는 얀센 코로나19 백신을 접종 받은 사람들에서 보고된 길랭-바레 증후군 108건에 대해 검토하였으며, 이 중 1건은 치명적이었음\n* ‘21.6.30. 기준 전 세계적으로 동 백신이 2,100만회 이상 도즈가 투여됨\n\n❍ 코로나19를 예방하는 백신의 전반적인 유익성이 위험성을 여전히 상회함\n❍ 보건의료전문가들은 길랭-바레 증후군 상태의 심각성을 고려하여 조기 진단과 증상 완화 및 치료가 가능하도록 그 징후와 증상에 대해 주의를 기울여야 함\n❍ 식품의약품안전처는 보건의료전문가 및 백신 접종 대상자에게 관련 정보를 제공하기 위하여 안전성 서한을 배포하며\n- 국내외 이상사례 현황 및 안전성 정보를 종합하여 관련 주의사항 등을 제품설명서에 추가하는 등 필요한 조치를 취할 예정임\n\n￭ 조치사항\n의약품 안전성 서한(아스트라제네카, 얀센 백신_길랭바레증후군).pdf', 'admin', '2021-07-26 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=117&search_value=&search_term=&paging_value=2&setLine=&setHeader='),
	(49, '류마티스성관절염 치료제 "토파시티닙" 등 3개 성분 제제 안전성 정보', '2021-09-03\n\n￭ 요약\n의약품 안전성 서한 배포 알림[류마티스성관절염 치료제 토파시티닙 등 3개 성분 제제]\n\n￭ 상세정보\n1. 미국 식품의약품청(FDA)에서 발표한 류마티스성관절염 치료제 등으로 사용되는 "토파시티닙" 등 3개 성분 제제에 대한 안전성 정보(중증 심장 관련 질환, 암, 혈전, 사망 등의 위험 증가 경고)에 따라 붙임과 같이 안전성 서한을 배포하니,\n\n2. 관련 단체(협회) 등에서는 회원사 및 비회원사에 동 정보사항을 널리 전파해 주시기 바라며, 의약품등 사전·사후 관리 관련 기관(부서) 등에서는 관련 업무에 참고하시기 바랍니다.\n\n￭ 조치사항\n의약품 안전성 서한 배포 알림[류마티스성관절염 치료제 토파시티닙 등 3개 성분 제제]', 'admin', '2021-09-02 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=118&search_value=&search_term=&paging_value=2&setLine=&setHeader='),
	(50, '바레니클린 함유 완제의약품 3개사 6개 품목 자진회수(식약처 안전성 속보)', '2021-09-07\n\n￭ 요약\n㈜씨티씨바이오 제조(수탁 포함) `니코브렉정0.5mg` 등\n바레니클린 함유 완제의약품 3개사 6개 품목 자진회수\n\n￭ 상세정보\n❍ 1일 섭취허용량(37ng/일)을 설정하였고, 불순물 저감화전까지 한시적 출하허용기준(185ng/일) 이하의 제조번호만 출하 허용\n❍ 건강상 큰 영향이 없으므로 의약품 복용을 임의 중단하지 말고, 지속복용이나 대체 의약품 변경 여부 등은 의‧약사와 상담할 것\n* 대체 의약품 전환시 국민건강보험공단의 금연치료지원사업 활용\n❍ 국내 유통품 중 733ng/일을 초과한 ㈜씨티씨바이오에서 제조한 3개사 6개 품목 19개 제조번호 자진 회수\n* 회수 기준은 미국, 유럽 등과 동일\n❍ 의ㆍ약 관계자 여러분께서는 해당 품목의 사용을 중지하여 주시고 유통품 회수가 적절히 수행될 수 있도록 적극 협조하여 주시기 바라며,\n❍ 의·약전문가는 동 정보사항에 유의하여 해당 제품의 처방 및 사용을 중지하고 대체의약품을 사용할 것을 당부드림\n\n￭ 조치사항\n❍ ㈜씨티씨바이오에서 제조한 니코브렉정0.5mg(바레니클린살리실산염) 등 2개 품목 및 이와 동일한 방법으로 수탁제조하는 2개사 4개 품목에 대해 해당 제조번호 자진 회수', 'admin', '2021-09-06 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=119&search_value=&search_term=&paging_value=2&setLine=&setHeader='),
	(51, '로사르탄, 발사르탄, 이르베사르탄 성분 함유 완제의약품 자진회수(식약처 안전성 속보)', '2021-09-09\n\n￭ 요약\n❍ AZBT의 1일 섭취허용량은 1.5㎍/일로 설정하였고, 국내 유통품 중 36개사 73품목(183개 제조번호)에서 초과 검출되어, 해당 제조번호 자진 회수\n\n￭ 상세정보\n❍ 국내 AZBT 1일 섭취 허용량이 초과 검출된 사르탄류 의약품을 복용한 환자에 대한 인체영향평가 결과 추가적인 암 발생 가능성은 매우 낮은 수준이므로,\n❍ 이미 해당 제품(제조번호)을 처방받은 환자분들은 의약품 복용을 임의로 중단하지 말고, 계속 복용하거나 대체 의약품으로 전환 필요성 등은 반드시 의․약사와 상담할 것\n❍ 건강상 우려가 있는 경우 남은 의약품을 소지하고 조제약국에서 기준 이하 제조번호 제품으로 교환 가능\n- 기존에 해당 의약품을 조제한 약국을 방문할 수 없는 경우 다른 약국에서도 교환 가능\n- 방문 약국에서 기준 이하 제조번호 제품으로 교환이 어려운 경우 재방문하여 교환하거나,\n- 교환이 불가한 경우 해당 제약사 소비자상담실(붙임2)에 문의하도록 안내\n\n￭ 조치사항\n❍ 로사르탄, 발사르탄, 이르베사르탄 성분 함유 완제의약품 자진회수(36개사 73품목 183개 제조번호)', 'admin', '2021-09-08 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=120&search_value=&search_term=&paging_value=2&setLine=&setHeader='),
	(52, '제일약품㈜ 제조‘텔미듀오정40/5밀리그램’등 44개 품목 잠정 제조·판매·사용 중지(식약처 안전성 속보)', '2021-10-27\n\n￭ 요약\n❍ 제일약품㈜에서 제조하는 의약품 3개 품목과 동 업체에서 위탁 제조하는 의약품 41개 품목에 대해 잠정 제조·판매 중지 명령 및 사용중단을 요청함\n\n￭ 상세정보\n❍ 식품의약품안전처는 제일약품㈜에 대해 점검한 결과, 제일약품㈜이 ‘텔미듀오정40/5밀리그램’ 등 3개 품목의 허가를 위해 제출한 자료 중 잔류용매 시험 자료가 허위로 작성됐음을 확인하여, 약사법 위반으로 잠정 제조·판매 중지 및 회수 조치하고 품목허가 취소 절차에 착수함\n❍ 아울러 제일약품㈜에서 ‘텔미듀오정40/5밀리그램’ 등 3개 품목과 동일하게 위탁 제조하는 품목 중 동일한 허가 신청자료를 제출해 허가받은 14개사 41개 품목도 제일약품㈜ 3개 품목과 동일한 조치를 결정함\n\n￭ 조치사항\n❍ 제일약품㈜에서 제조한 ‘텔미듀오정40/5밀리그램’ 등 3개 품목 및 동 업체에서 위탁 제조하는 14개사 41개 품목에 대해 잠정 제조·판매 중지 명령 및 회수', 'admin', '2021-10-26 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=121&search_value=&search_term=&paging_value=2&setLine=&setHeader='),
	(53, '국가출하승인 위반 의약품 회수․폐기 및 사용 중지 등(식약처 안전성 속보)', '2021-11-10\n\n￭ 요약\n국가출하승인 위반 의약품(2개 업체 6개 품목) 회수․폐기 및 사용 중지 등 조치\n\n￭ 상세정보\n❍ 식품의약품안전처는 국가출하승인 대상인 보툴리눔 제제를 국가출하승인을 받지 않고 국내에 판매하여 약사법을 위반한 2개 업체 6개 품목의 해당 제조번호에 대해 회수․폐기 명령 등 조치하고 품목허가 취소절차 착수함\n- 휴젤주식회사 4개 품목(보툴렉스주, 보툴렉스주50단위, 보툴렉스주150단위, 보툴렉스주200단위)\n- ㈜파마리서치바이오 2개 품목(리엔톡스주100단위, 리엔톡스주200단위)', 'admin', '2021-11-09 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=122&search_value=&search_term=&paging_value=2&setLine=&setHeader='),
	(54, '“아데노실·메티오닌 황산토실산염”제제에 대한 ‘퇴행성관절염’적응증 사용제한 권고 (식약처 안전성 서한)', '2021-11-24\n\n￭ 요약\n의약품 안전성 서한 배포 알림(S-아데노실-L-메티오닌황산토실산염 제제)\n\n￭ 상세정보\n1. 식품의약품안전처에서「약사법」제33조에 따라 실시한 "S-아데노실-L-메티오닌황산토실산염" 제제에 대한 임상재평가와 관련하여 관련 업체에서 제출한 임상시험결과,\n해당 품목의 효능∙효과 중 \'퇴행성 관절증\'의 유효성이 입증되지 않아 해당 적응증 사용을 제한하는 내용으로 붙임과 같이「의약품 안전성 서한」을 송부합니다.\n\n2. 관련 단체(협회) 등에서는 회원사 등에 동 정보사항을 널리 전파해 주시기 바라며, 의약품등 사전·사후 관리 기관(부서) 등에서는 관련 업무에 참고하시기 바랍니다.\n\n￭ 조치사항\n\n의약품 안전성 서한 배포 알림(S-아데노실-L-메티오닌황산토실산염 제제)', 'admin', '2021-11-23 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=123&search_value=&search_term=&paging_value=2&setLine=&setHeader='),
	(55, '메디카코리아 제조 수탁 포함 \'록펜정\' 등 12개 품목 회수 폐기 등 조치 (식약처 안전성 속보)', '2021-11-26\n\n￭ 요약\n❍ ㈜메디카코리아에서 제조한 의약품 5개 품목과 동 업체에서 수탁 제조한 의약품 7개 품목에 대해 회수·폐기 조치 및 사용중단을 요청하며,\n❍ 이 중 제조원이 ㈜메디카코리아에서 다른 제조소로 변경된 5개 품목을 제외한 7개 품목에 대해서는 잠정 제조·판매 중지 조치함\n\n￭ 상세정보\n❍ ㈜메디카코리아에서 제조한 의약품 5개 품목과 동 업체에서 수탁 제조한 의약품 7개 품목에 대해 회수·폐기 조치 및 사용중단을 요청하며,\n❍ 이 중 제조원이 ㈜메디카코리아에서 다른 제조소로 변경된 5개 품목을 제외한 7개 품목에 대해서는 잠정 제조·판매 중지 조치함\n\n￭ 조치사항\n❍ ㈜메디카코리아에서 제조한 의약품 5개 품목과 동 업체에서 수탁 제조한 의약품 7개 품목에 대해 회수·폐기 조치 및 사용중단을 요청하며,\n❍ 이 중 제조원이 ㈜메디카코리아에서 다른 제조소로 변경된 5개 품목을 제외한 7개 품목에 대해서는 잠정 제조·판매 중지 조치함', 'admin', '2021-11-25 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=124&search_value=&search_term=&paging_value=2&setLine=&setHeader='),
	(56, '국가출하승인 위반 의약품)에 대한 품목허가 취소 및 회수·폐기 조치(식약처 안전성 속보)', '2021-12-02\n\n￭ 요약\n국가출하승인 위반 의약품(2개 업체 6개 품목)에 대한 품목허가 취소 및 회수·폐기 조치\n\n￭ 상세정보\n식품의약품안전처는 국가출하승인 대상인 보툴리눔 제제를 국가출하승인을 받지 않고 국내에 판매한 2개 업체 6개 품목에 대해 2021. 12. 13.자로 품목허가를 취소하고 회수·폐기 조치함\n- 휴젤주식회사 4개 품목(보툴렉스주, 보툴렉스주50단위, 보툴렉스주150단위, 보툴렉스주200단위)\n- ㈜파마리서치바이오 2개 품목(리엔톡스주100단위, 리엔톡스주200단위)', 'admin', '2021-12-01 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=125&search_value=&search_term=&paging_value=2&setLine=&setHeader='),
	(57, '로사르탄 성분 함유 완제의약품 자진회수 (98개사 295품목 전체 또는 일부 제조번호) (식약처 안전성 속보)', '2021-12-07\n\n￭ 요약\n로사르탄 성분 함유 완제의약품 자진회수 (98개사 295품목 전체 또는 일부 제조번호)\n\n￭ 상세정보\n❍ 식약처에서 `로사르탄 아지도 불순물` 1일 섭취 허용량이 초과 검출된 로사르탄 의약품을 복용한 대다수 환자의 건강상 영향을 평가한 결과, 추가적인 암 발생 가능성은 매우 낮은 수준이므로,\n- 해당 의약품을 복용한 환자들은 임의로 복용을 중단하지 말고 의·약사 상담 후 복용 지속 여부 등을 결정해야 하며,\n- 필요시 다른 제조번호로 교환하거나, 다른 제품으로 재처방·재조제가 가능\n❍ 의ㆍ약 관계자 여러분께서는 회수 대상 제품(제조번호)의 사용을 중지하여 주시고 유통품 회수가 적절히 수행될 수 있도록 적극 협조하여 주시기 바라며,\n❍ 의·약전문가는 동 정보사항에 유의하여 불순물이 1일 섭취허용량을 초과하지 않은 제품(붙임참고), 또는 대체 가능한 다른 의약품을 사용할 것을 당부드림\n\n￭ 조치사항\n❍ 로사르탄 아지도 불순물의 1일 섭취허용량은 1.5㎍/일로 설정하였고, 국내 유통품 중 98개사 295품목의 전체 또는 일부 제조번호에서 초과 검출되거나 초과 검출이 우려되어, 해당 제품 자진 회수', 'admin', '2021-12-06 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=126&search_value=&search_term=&paging_value=2&setLine=&setHeader='),
	(58, '㈜비보존제약 제조(수탁 포함) \'데코라펜정\' 등 13개 품목 회수·폐기 등 조치(식약처 안전성 속보)', '2021-12-27\n\n￭ 요약\n❍ (주)비보존제약에서 제조한 의약품 2개 품목과 동 업체에서 수탁 제조한 의약품 11개 품목에 대해 회수·폐기 조치 및 사용중단을 요청하며\n❍ 이 중, 제조원이 (주)비보존제약에서 다른 제조소로 변경된 11개 품목을 제외한 2개 품목에 대해서는 잠정 제조·판매 중지 조치함\n\n￭ 상세정보\n❍ 식품의약품안전처는 (주)비보존제약에서 제조(수탁 포함)한 \'데코라펜정\' 등 의약품 13개 품목 관련 해당 제조소에 대한 현장 조사 결과, 허가 또는 신고된 사항 등과 다르게 제조하고 있는 것을 확인하였으며, 사전 예방적 차원에서 (주)비보존제약에서 제조한\n13개 품목에 대하여 회수·폐기 조치함\n\n￭ 조치사항\n❍ 비보존제약에서 제조한 \'데코라펜정\' 등 2개 품목 및 동 업체에서 수탁 제조한 11개사 11개 품목', 'admin', '2021-12-26 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=128&search_value=&search_term=&paging_value=1&setLine=&setHeader='),
	(59, '㈜메디카코리아 제조(수탁 포함) “메디카레바시드정”등 18개 품목 잠정 제조·판매·사용 중(식약처 안전성 속보)', '2022-03-08\n\n￭ 요약\n❍ ㈜메디카코리아에서 제조한 4개 품목과 동 업체에서 수탁 제조한 14개 의약품에 대해 잠정 제조·판매 중지 명령 및 사용중단을 요청함\n\n￭ 상세정보\n❍ 식품의약품안전처는 ㈜메디카코리아에 대한 현장 조사결과, “메디카레바시드정” 등 18개 품목이 허가 또는 신고된 사항과 다르게 제조(수탁 포함)하고 있는 사실이 확인됨에 따라 사전 예방적 차원에서 잠정 제조·판매\n중지를 명령하고 회수 조치함\n\n￭ 조치사항\n❍ ㈜메디카코리아에서 제조한 ‘메디카레바시드정’ 등 4개 품목과 동 업체에서 수탁 제조한 13개사 14개 품목', 'admin', '2022-03-07 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=129&search_value=&search_term=&paging_value=1&setLine=&setHeader='),
	(60, '광동제약㈜ ‘베니톨정’ 자진회수(식약처 안전성 속보)', '2022-03-11\n\n￭ 요약\n미세정제플라보노이드분획물 성분 함유 완제의약품 자진회수 (1개사 1품목 일부 제조번호)\n\n￭ 상세정보\n❍ 혈관보강제인 광동제약㈜의 ‘베니톨정(미세정제플라보노이드분획물)’의 일부 제조번호에서 N-nitrosomorpholine(NMOR) 이 1일 섭취허용량(127ng/일) 초과 검출\n❍ 건강상 큰 영향이 없으므로 의약품 복용을 임의 중단하지 말고, 지속복용이나 대체의약품 변경 여부 등은 의‧약사와 상담할 것\n- 건강상 우려가 있는 경우 조제약국 등에서 기준 이하 제조번호 제품으로 교환하거나 제약사 소비자상담실에 문의\n❍ 의‧약 관계자 여러분께서는 해당 제조번호 제품의 사용을 중지하여 주시고 유통품 회수가 적절히 수행될 수 있도록 적극 협조하여 주시기 바라며,\n❍ 의‧약전문가는 동 정보사항에 유의하여 다른 제조번호 제품이나 동일 성분의 다른 의약품을 사용할 것을 당부드림\n\n￭ 조치사항\n❍ NMOR의 1일 섭취허용량은 기준 초과 제품에 대해 자진 회수', 'admin', '2022-03-10 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=130&search_value=&search_term=&paging_value=1&setLine=&setHeader='),
	(61, '한약재 ‘빈랑자’, ‘대복피’ 사용 주의', '2022-05-23\n\n￭ 요약\n- 한약재 빈랑자 및 대복피 사용 시 사람에게 암을 유발한다는 임상적 증거는 불충분하며, 해당 한약재 및 한약(생약)제제의 복용 형태를 감안한 빈랑자·대복피 추출물 및 분말에 대한 인체 위해평가 실시 사례를 확인할 수 없었음\n- 현재 정보만으로 해당 한약재의 위해성을 판단하기에 어려우며, 치료의 유익성과 잠재적 위험성을 평가해야 한다는 중앙약사심의위원회 자문 결과 등을 고려하여, 식품의약품안전처는 빈랑자 및 대복피 관련 인체 위해평가를 위한 연구를 실시할 예정임\n\n￭ 상세정보\n❍ 정보원\n- 세계보건기구(WHO) 산하 국제암연구소(IARC)에서 한약재 빈랑자 및 대복피에 함유되어 있는 ‘아레콜린’을 발암가능물질 등급(Group 2B)*으로 분류\n* 사람에게 암을 유발한다는 임상적 증거는 불충분함\n\n❍ 주요내용\n- 한약재 빈랑자 및 대복피 사용 시 사람에게 암을 유발한다는 임상적 증거는 불충분하며, 해당 한약재 및 한약(생약)제제의 복용 형태를 감안한 빈랑자·대복피 추출물 및 분말에 대한 인체 위해평가 실시 사례를 확인할 수 없었음\n- 현재 정보만으로 해당 한약재의 위해성을 판단하기에 어려우며, 치료의 유익성과 잠재적 위험성을 평가해야 한다는 중앙약사심의위원회 자문 결과 등을 고려하여, 식품의약품안전처는 빈랑자 및 대복피 관련 인체 위해평가를 위한 연구를 실시할 예정임\n- 참고로, 최근 6년간 보고된 해당 한약재 및 제제에 대한 이상사례는 총 6건으로 중대한 이상사례는 없었으며, 이상사례와 해당 한약재와의 인과관계도 확인되지 않았음\n\n❍ 대상 의약품\n- 한약재 빈랑자 83품목, 대복피 76품목, 빈랑자 또는 대복피 함유 완제의약품 8품목(붙임 참조)\n\n￭ 조치사항\n❍ 전문가를 위한 정보\n- 치료상의 유익성이 잠재적 위험성을 상회한다고 판단되는 경우에 투여할 것\n- 환자의 증상에 따라 필요한 최소량만을 사용할 것\n- 필요한 경우 환자에게 동 정보사항에 대해 알리기 바람\n- 대상 의약품 사용으로 부작용이 나타나는 경우 한국의약품안전관리원으로 보고하시기 바람\n\n❍ 환자를 위한 정보\n- 대상 의약품 사용으로 부작용이 나타나는 경우 한국의약품안전관리원으로 보고하시기 바람\n- 처방 받은 환자분들 중에서 건강에 대한 우려가 있는 분들은 해당 의약품을 처방한 병·의원, 약국 등의 한의사·약사 등과 상담하시기 바람', 'admin', '2022-05-22 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=131&search_value=&search_term=&paging_value=1&setLine=&setHeader='),
	(62, '㈜퍼슨 의약품 제조판매품목 인카인겔 사용중지 등 조치', '2022-05-25\n\n￭ 요약\n❍ ㈜퍼슨 의약품 제조판매품목 ‘인카인겔’에 대해 사용중단을 요청함\n\n￭ 상세정보\n❍ 식품의약품안전처는 ㈜퍼슨에서 제조한 국소마취제 “인카인겔”에 대한 수거·검사 결과, ‘에틸렌클로로히드린(2-CE)’ 검출이 확인됨에 따라\n- 해당 업체에서 자체적으로 진행하고 있는 시중 유통품에 대한 자진회수와 자발적 출하 중단과 병행하여 사전 예방적 차원에서 사용 중지 조치함\n\n￭ 조치사항\n㈜퍼슨 의약품 제조판매품목 ‘인카인겔’에 대한 사용중단 등 조치', 'admin', '2022-05-24 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=132&search_value=&search_term=&paging_value=1&setLine=&setHeader='),
	(63, '경방신약, 한솔신약 제조 48개 품목 제조·판매 중지, 회수 조치', '2022-06-02\n\n￭ 요약\n경방신약㈜ 및 한솔신약㈜에서 제조한 의약품 39개 품목 및 동 업체들에서 수탁 제조한 의약품 9개 품목에 대해 잠정 제조·판매 중지 및 회수 등 조치함\n\n￭ 상세정보\n식품의약품안전처는 경방신약㈜에서 제조한 ‘경방갈근탕액(갈근탕액)’ 등 41개 품목 및 한솔신약㈜에서 제조한 ‘배낙스정’ 등 7개 품목 관련 해당 제조소에 대한 현장 조사 결과, 제조방법을 변경하지 않고 의약품을 제조하는 등 약사법 위반사항을 확인하였으며, 사전예방적 차원에서 해당 48개 품목에 대하여 잠정 제조·판매 중지, 회수 등 조치함\n\n￭ 조치사항\n경방신약㈜에서 제조한 ‘경방갈근탕액(갈근탕액)’ 등 32개 품목과 한솔신약㈜에서 제조한 ‘배낙스정’ 등 7개 품목 및 동 업체들에서 수탁 제조한 3개사 9개 품목 잠정 제조·판매 중지, 회수 등 조치함', 'admin', '2022-06-01 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=133&search_value=&search_term=&paging_value=1&setLine=&setHeader='),
	(64, '아세틸-엘-카르니틴염산염\' 제제 사용 중지 권고', '2022-08-05\n\n￭ 상세정보\n1. 우리 처에서「약사법」제33조에 따라 실시한 "아세틸-엘-카르니틴" 제제에 대한 임상재평가와 관련하여 업체에서 제출한 임상시험 결과,\n해당 품목의 효능∙효과 \'뇌혈관 질환에 의한 이차적 퇴행성 질환\'에 대한 유효성이 입증되지 않아 대체의약품 사용을 권고하는 내용으로 붙임과 같이「의약품 정보 서한」을 송부합니다.\n\n2. 관련 단체(협회) 등에서는 회원사 등에 동 정보사항을 널리 전파해 주시기 바라며, 의약품등 사전·사후 관리 기관(부서) 등에서는 관련 업무에 참고하시기 바랍니다.\n\n\n붙임. 의약품 정보 서한 1부. 끝', 'admin', '2022-08-04 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=134&search_value=&search_term=&paging_value=1&setLine=&setHeader='),
	(65, '케이엠에스제약 제조 38개품목 잠정 제조·판매·사용 중지', '2022-10-20\n\n￭ 요약\n❍ 케이엠에스제약㈜에서 제조한 9개 품목과 동 업체에서 수탁 제조한 29개 의약품에 대해 잠정 제조·판매 중지 명령 및 사용중단을 요청함\n\n￭ 상세정보\n❍ 식품의약품안전처는 케이엠에스제약㈜에 대한 현장 조사결과, “레바코스정” 등 38개 품목이 허가 또는 신고된 사항과 다르게 제조(수탁 포함)되고 있는 사실이 확인됨에 따라 사전 예방적 차원에서 잠정 제조·판매 중지를 명령하고 회수 조치함\n\n￭ 조치사항\n❍ 케이엠에스제약㈜에서 제조한 ‘레바코스정’ 등 9개 품목과 동 업체에서 수탁 제조한 20개사 29개 품목', 'admin', '2022-10-19 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=135&search_value=&search_term=&paging_value=1&setLine=&setHeader='),
	(66, '국가출하승인 위반 의약품 3개품목 회수·폐기', '2022-11-01\n\n￭ 요약\n식품의약품안전처는 국가출하승인 위반 의약품(3개사 3개 제품)에 대해 회수․폐기 명령 등 조치하고 품목허가 취소절차 착수함\n\n￭ 상세정보\nㅇ 식품의약품안전처는 국가출하승인 대상인 보툴리눔 제제를 국가출하승인을 받지 않고 국내에 판매하여 약사법을 위반한 3개 업체 3개 품목에 대해 회수․폐기 명령 등 조치하고 품목허가 취소절차 착수함\n- ㈜제테마 제테마더톡신주100IU(수출용)\n- ㈜한국비엠아이 하이톡스주100단위(수출용)\n- ㈜한국비엔씨 비에녹스주(수출용)', 'admin', '2022-10-31 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=136&search_value=&search_term=&paging_value=1&setLine=&setHeader='),
	(67, '‘옥시라세탐’제제 사용 중지 권고', '2023-01-16\n\n￭ 상세정보\n1. 우리 처에서 「약사법」 제33조에 따라 실시한 "옥시라세탐" 제제에 대한 임상재평가와 관련하여 업체에서 제출한 임상시험 결과, 해당 품목의 효능·효과 \'혈관성 인지 장애 증상 개선\'에 대한 유효성이 입증되지 않아 대체의약품 사용을 권고하는 내용으로 붙임과 같이 「의약품 정보 서한」을 송부합니다.\n\n2. 관련 단체(협회) 등에서는 회원사 등에 동 정보사항을 널리 전파해 주시기 바라며, 의약품등 사전·사후 관리 기관(부서) 등에서는 관련 업무에 참고하시기 바랍니다.', 'admin', '2023-01-15 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=137&search_value=&search_term=&paging_value=1&setLine=&setHeader='),
	(68, '진균 검사(GM test) 가짜양성 유발 수액제 안전조치', '2023-03-24\n\n￭ 요약\n중국 Shandong Tianli社 포도당이 함유된 의약품(수액제)을 투여받은 환자 중 혈청 갈락토만난 항원 검사(Galactomannan, GM test)를 실시하는 경우 제품의 사용 및 검사 결과의 해석에 주의 요구\n\n￭ 상세정보\n❍ 식품의약품안전처는 최근 일부 수액제 투여 환자 중 진균 검사 시 ‘위양성(가짜 양성)’ 발생 보도와 관련하여 조사를 실시하였으며,\n- 이러한 위양성 발생 원인은 해당 수액제 제조 시 사용되는 원료(포도당) 생산과정에서 미량으로 잔류하는 galactomannan이 위양성 반응을 유발하는 것으로 판단하였음\n❍ 식품의약품안전처는 그 간의 조사결과 동 원료를 사용하여 제조된 의약품 총 14품목(붙임)의 품질은 허가된 기준에 적합한 것으로 확인되었고, 위양성 발생 가능성은 여타 식품, 항생제 등 다양한 제품에서 기인되고 있다는 문헌 등을 참조하여 해당 제품에 대한 회수 조치는 실시하지 않음\n\n￭ 조치사항\n의료 현장의 혼란을 방지하기 위해 해당 원료의 사용을 중지하고, 관련 업체에서는 해당 제품 정보를 의료기관 등에 제공토록 하여 필요시 교환 등을 할 수 있도록 제약사에 조치함', 'admin', '2023-03-23 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=138&search_value=&search_term=&paging_value=1&setLine=&setHeader='),
	(69, '‘세프테졸나트륨’주사제 사용 중지 권고', '2023-03-30\n\n￭ 상세정보\n1. 우리 처에서 「약사법」 제33조에 따라 실시한 "세프테졸나트륨" 제제에 대한 임상재평가와 관련하여 업체에서 제출한 임상시험 결과, 해당 품목의 효능·효과 \'복잡성 요로감염, 신우신염\'에 대한 유효성이 입증되지 않아 대체의약품 사용을 권고하는 내용으로 붙임과 같이 「의약품 정보 서한」을 송부합니다.\n\n2. 관련 단체(협회) 등에서는 회원사 등에 동 정보사항을 널리 전파해 주시기 바라며, 의약품등 사전·사후 관리 기관(부서) 등에서는 관련 업무에 참고하시기 바랍니다.\n\n붙임. 의약품 정보 서한 1부. 끝.', 'admin', '2023-03-29 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=139&search_value=&search_term=&paging_value=1&setLine=&setHeader='),
	(70, '한국노바티스(주), 씨뮬렉트주사 첨부용제(주사용수) 회수', '2023-04-12\n\n￭ 상세정보\n한국노바티스(주), 씨뮬렉트주사 첨부용제(주사용수) 회수', 'admin', '2023-04-11 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=140&search_value=&search_term=&paging_value=1&setLine=&setHeader='),
	(71, '대원제약 \'콜대원키즈펜시럽(아세트아미노펜)\'등 2품목 회수 권고 및 사용 시 주의 요청', '2023-05-17\n\n￭ 요약\n❍ 대원제약㈜ “콜대원키즈펜시럽(아세트아미노펜)” 및 ㈜다나젠 “파인큐아세트펜시럽(아세트아미노펜)”에 대해 잠정 제조·판매중지 및 영업자 회수 권고 조치(5.17)\n\n￭ 상세정보\n❍ 식품의약품안전처는 “아세트아미노펜” 액상시럽제 및 현탁제 제조 및 수입업체에 대한 점검결과,\n- 제조공정 및 품질관리 측면에서 위반사항은 발견되지 않았고, 전문가 자문결과 제형의 특성 상 일부 상분리 현상이 발생할 수 있고 투여량 편차에 따른 실제 위험성은 낮다고 판단됨\n- 다만, 상분리 제품의 품질저하 우려 및 적정 용량 투여가 어려운 점 등을 감안하여 ‘상분리’ 현상이 확인된 2개 품목에 대하여 사전예방적 조치로서 자발적 회수를 권고하였음\n* 상분리 : 투명액(맑은액)과 불투명액(흰색)으로 분리되는 현상\n- 또한, 상분리 현상이 발생하지 않도록 제제개선 등이 완료될 때까지 잠정적으로 제조‧판매 중지하였음\n\n￭ 조치사항\n[의약전문가를 위한 권고사항]\n❍ 해당 제품의 처방 및 판매를 중지하고, 상기 내용을 감안하여 사용 시 주의를 기울여 주시기 바람\n❍ 필요 시 대체 가능한 의약품을 사용할 것\n❍ 해당 제품 사용으로 인해 발생하는 부작용은 한국의약품안전관리원에 보고하시기 바람\n\n[환자를 위한 권고사항]\n❍ 해당 제품을 구입했거나 사용중인 환자는 해당 업체 대표누리집 또는 고객센터를 통해 반품과 환불이 가능함\n❍ 대체 가능한 의약품 등에 대하여 의사 또는 약사와 상담하고 해당 제품 사용으로 인해 발생하는 부작용은 한국의약품안전관리원에 보고하시기 바람', 'admin', '2023-05-16 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=141&search_value=&search_term=&paging_value=1&setLine=&setHeader='),
	(72, '휴온스바이오파마 리즈톡스주100단위 회수․폐기 및 잠정 제조·판매·사용 중지', '2023-06-02\n\n￭ 요약\n식품의약품안전처는 국가출하승인 위반 의약품(㈜휴온스바이오파마 리즈톡스주100단위(클로스트리디움보툴리눔독소A형))에 대해 회수․폐기 명령 등 조치하고 품목허가 취소절차 착수함\n\n￭ 상세정보\n식품의약품안전처는 국가출하승인 대상인 보툴리눔 제제를 국가출하승인을 받지 않고 국내에 판매하여 약사법을 위반한 ㈜휴온스바이오파마의 리즈톡스주100단위(클로스트리디움보툴리눔독소A형)에 대해 회수․폐기 명령 등 조치하고 품목허가 취소 등 행정처분 절차에 착수함', 'admin', '2023-06-01 15:00:00', 'https://www.health.kr/notice/safety_view.asp?show_idx=142&search_value=&search_term=&paging_value=1&setLine=&setHeader=');
